INSERT IGNORE INTO competency (id_comp, nama, deskripsi) VALUES
('1', 'Pengelolaan Remunerasi', 'Kemampuan mengelola dan mengembangkan sistem remunerasi yang meliputi Administrasi Personalia dan Sistem Penggajian (kompensasi, benefit dan payroll) diperusahaan untuk meningkatkan kesejahteraan karyawan.'),
('2', 'Pengelolaan Kesehatan Dan Keselamatan Kerja', 'Pengetahuan dan kemampuan manajerial dan teknis terhadap operasional health and safety (H&S) berpedoman pada ketentuan peraturan dan perundangan H&S yang berlaku sebagai upaya pencegahan kecelakaan, kebakaran, penyakit akibat kerja, serta kerusakan aset perusahaan.'),
('3', 'Pengelolaan Kinerja', 'Kemampuan melakukan perencanaan, pengelolaan dan evaluasi proses kinerja individual dan organisasi berdasarkan sasaran kerja yang telah ditetapkan.'),
('4', 'Pengelolaan Karir', 'Kemampuan untuk merencanakan, mengelola, mengevaluasi dan mengembangkan karir individual berdasarkan potensi dan kompetensi.'),
('5', 'Pengembangan Organisasi', 'Pemahaman dan kemampuan tentang peran dan perilaku organisasi serta penerapannya di perusahaan.'),
('6', 'Pengelolaan Pelatihan', 'Kemampuan merencanakan, mengelola, mengevaluasi dan mengembangkan mekanisme pelatihan berdasarkan analisa kompetensi individu dan kebutuhan perusahaan untuk meningkatkan produktifitas kerja.'),
('7', 'Pengelolaan Mutu Usaha', 'Pengetahuan dan kemampuan untuk menjalankan, mengelola, mengevaluasi dan memodifikasi Pengelolaan Mutu Usaha.'),
('8', 'Kemampuan Berbahasa Lisan Dan Atau Tulisan', 'Kemampuan berbahasa Berkomunikasi efektif baik lisan dan/atau tulisan secara memadai bagi kebutuhan audiens.'),
('9', 'Kemampuan Komunikasi', 'Kemampuan untuk menyampaikan dan menerima pesan dengan jelas kepada target audiencenya.'),
('10', 'Pengetahuan Dan Keterampilan Hukum', 'Pengetahuan hukum dan perundang-undangan, kontrak/ perjanjian, kebijakan dan prosedur ber acara serta kemampuan menerapkannya dalam kegiatan bisnis dan korporasi perusahaan.'),
('11', 'Pengelolaan Operasional', 'Kemampuan untuk melakukan, mengelola, menyusun rencana operasional sesuai strategi bisnis perusahaan terhadap aktivitas di masing-masing unit dan antar unit terkait agar aktivitas berjalan dengan lancar, cepat dan sesuai dengan target produktivitas dan waktu yang ditetapkan dengan menggunakan sistem yang telah ditetapkan oleh perusahaan.'),
('12', 'Penanganan Pelanggan', 'Pengetahuan dan kemampuan melakukan, mengelola dan menyusun rencana strategis untuk mengelola aktivitas pelayanan pelanggan (keluhan, layanan informasi dsb) untuk meningkatkan kepuasan dan loyalitas pelanggan sesuai dengan target yang ditetapkan perusahaan.'),
('13', 'Pengelolaan Akun', 'Kemampuan mengelola dan memelihara akun kepelangganan, mengoptimalisasi penggunaan produk dan jasa, menyusun strategi penjualan produk dan jasa, optimalisasi penggunaan produk dan jasa terhadap pelanggan (regular, key account) sesuai rencana strategis untuk mencapai target revenue perusahaan.'),
('14', 'Pengelolaan Pemasaran', 'Pengetahuan dan kemampuan dalam menentukan konsep, mengelola, menyusun strategi aktivitas pemasaran yang inovatif terkait aktivitas promosi, branding, calendar of event, dan melakukan riset untuk meningkatkan image, dan brand perusahaan.'),
('15', 'Pengelolaan Strategi', 'Kemampuan untuk menentukan arah, rencana, strategi baru berdasarkan kondisi eksternal (peraturan pemerintah, PKS, makro ekonomi, dll) dan performance internal.'),
('16', 'Pengelolaan Aset', 'Pengetahuan dan kemampuan untuk melakukan analisis tentang nilai dan manfaat untuk rencana pengembangan fixed asset secara optimal.'),
('17', 'Pengelolaan Proyek', 'Pengetahuan dan Kemampuan untuk melaksanakan pengelolaan proyek untuk mendapatkan hasil yang optimal bagi kebutuhan operasional perusahaan.'),
('18', 'Kemampuan Negosiasi', 'Kemampuan untuk menyatukan pendapat dan menyelesaikan perbedaan kepentingan sesuai ketentuan yang berlaku.'),
('19', 'Pengelolaan Tagihan', 'Pengetahuan dan Kemampuan mengelola Operasional billing / proses pembuatan tagihan yang berhubungan dengan kepelangganan sesuai target yang di tetapkan.'),
('20', 'Pengelolaan Penagihan', 'Pengetahuan dan kemampuan untuk mengelola Operasional proses penagihan sesuai target yang ditentukan.'),
('21', 'Pengelolaan Kas', 'Pengetahuan tentang perencanaan, evaluasi dan implementasi yang berhubungan dengan kaidah dan kebijakan yang menyangkut prinsip dan efisiensi perusahaan dalam hal pengelolaan, pengendalian, perencanaan uang masuk dan keluar termasuk operasi'),
('22', 'Pelaporan Keuangan', 'Pengetahuan tentang dasar-dasar akuntasi, pembukuan, pencatatan, pembuatan serta penyajian informasi keuangan sesuai kondisi dan kinerja keuangan secara wajar berdasar Prinsip Akuntansi Nasional (PSAK) maupun International (GAAP dan IAS).'),
('23', 'Anggaran Dan Kontrol Biaya', 'Kemampuan merencanakan anggaran, pengeluaran dan pendapatan, rekomendasi untuk mencapai rencana tersebut, serta pengendalian terhadap penyimpangan anggaran.'),
('24', 'Perpajakan', 'Kemampuan menerapkan dan mengendalikan pajak perusahaan agar dapat memberikan manfaat dan ketaatan pajak yang maksimal untuk mengantisipasi perubahan UU Pajak terhadap kebijakan keuangan perusahaan.'),
('25', 'Analisa Investasi', 'Kemampuan mengukur nilai tambah proyek investasi.'),
('26', 'Pengelolaan Resiko', 'Kemampuan untuk memprediksi resiko dalam setiap rencana tindakan, dan menetapkan kebijakan antisipasinya.'),
('27', 'Audit', 'Kemampuan untuk melakukan kegiatan pemeriksaan, pengujian, penilaian, investigasi dan memberikan rekomendasi secara objektif dan independen untuk menjaga efisiensi dan efektifitas kinerja perusahaan yang sesuai dengan kebijakan, standard operasional perusahaan, standard audit dan undang-undang yangg berlaku secara sistematis dan terstruktur.'),
('28', 'Pengelolaan Pengadaan', 'Kemampuan dalam hal proses pengadaan barang dan jasa yang dibutuhkan dengan memenuhi aspek Quality, Cost, Service dan Delivery (QCSD) untuk mendukung operasional perusahaan.'),
('29', 'Pengelolaan Persediaan', 'Kemampuan perencanaan kebutuhan dan pengendalian persediaan barang sehingga menjaga kelangsungan penyediaan barang untuk mendukung operasional perusahaan.'),
('30', 'Pengelolaan Logistik', 'Kemampuan mengelola proses pergudangan dan distribusi barang serta implementasinya di perusahaan.'),
('31', 'Pengelolaan Keamanan', 'Kemampuan menganalisa, merencanakan dan melaksanakan sistem pengamanan aset termasuk mencegah dan menanggulangi terjadinya tindakan kejahatan terhadap aset perusahaan.'),
('32', 'Pengelolaan Transportasi', 'Kemampuan merencanakan, mengorganisir, melaksanakan dan mengontrol sarana transportasi secara maksimal dan efektif bagi kepentingan perusahaan.'),
('33', 'Pengelolaan Perkantoran', 'Kemampuan merencanakan, mengorganisir, melaksanakan dan mengontrol fasilitas dan kegiatan perkantoran secara efektif dan efisien untuk kepentingan perusahaan.'),
('34', 'Pengelolaan Dokumen', 'Kemampuan merencanakan, mengorganisir, melaksanakan dan mengontrol dokumen untuk menunjang kelancaran bisnis perusahaan sesuai undang-undang yang berlaku.'),
('35', 'Kemampuan Berbahasa Inggris', 'Pengetahuan dan kemampuan berkomunikasi dengan menggunakan Bahasa Inggris.'),
('36', 'Pengelolaan Kontrak', 'Pengetahuan dan kemampuan melakukan pengikatan dengan pihak ketiga, kemampuan menyusun (legal drafting), mengelola dan menjalankan administrasi perjanjian/kontrak (tidak termasuk proses penunjukan).'),
('37', 'Kemampuan Kehumasan', 'Pengetahuan dan kemampuan untuk menjaga hubungan dengan para pemangku kepentingan melalui serangkaian aktivitas yang diperlukan dalam rangka menjaga reputasi perusahaan mencakup: media relations, internal/external relations, event management, community development, CSR, dsb.'),
('38', 'Perancangan Aplikasi TI', 'Kemampuan untuk merancang suatu sistem aplikasi bisnis untuk memenuhi kebutuhan perusahaan serta menentukan kebutuhan infrastruktur yang terkait dengan aplikasi tersebut.'),
('39', 'Pemograman', 'Kemampuan untuk menterjemahkan rancangan aplikasi bisnis menjadi program komputer yang dibutuhkan perusahaan.'),
('40', 'Keterampilan Komputer', 'Kemampuan untuk mengoperasikan peralatan komputer seperti desktop, notebook, perangkat lunak pendukungnya dan peripheral lain.'),
('41', 'Jaringan Komunikasi', 'Kemampuan menerapkan jaringan komunikasi data seperti internet maupun komunikasi suara, baik melalui kabel maupun nirkabel.'),
('42', 'Administrasi Sistem Komputer', 'Kemampuan merancang dan mengelola server berikut komponennya, seperti storage dan perangkat lunak pendukung, termasuk data center dan perlengkapannya.'),
('43', 'Orientasi Pada Prestasi', 'Keinginan dan usaha untuk bekerja lebih baik atau mengarah pada standar keunggulan kinerja. Catatan: standar tersebut bisa berupa prestasi sendiri di masa lampau (perbaikan), ukuran yang obyektif (orientasi hasil), kinerja orang lain (kompetisi), sasaran yang menantang, atau sesuatu yang belum pernah dilakukan orang lain (inovasi).'),
('44', 'Inisiatif', 'Kemauan untuk bertindak lebih dari sekedar yang dibutuhkan atau diminta dengan tujuan untuk memperbaiki dan meningkatkan mutu, mengantisipasi masalah atau menciptakan kesempatan baru. Catatan: Inisiatif mengandung pengertian dalam konteks bertindak proaktif dan bukan sekedar berpikir tentang suatu tindakan yang harus diambil di masa datang'),
('45', 'Kepedulian Terhadap Keteraturan', 'Dorongan mendasar dari dalam diri untuk mengurangi ketidakpastian di lingkungan kerja dengan menjamin keteraturan dan ketepatan, memantau dan memeriksa pekerjaan maupun informasi, serta menekankan pada kejelasan peran, fungsi dan sebagainya'),
('46', 'Pencarian Informasi', 'Keinginan dan usaha yang dilakukan terus menerus untuk mengetahui dan memperoleh informasi lebih banyak tentang sesuatu, seseorang atau permasalahan lebih daripada yang dituntut dalam pelaksanaan tugas/pekerjaan dan pengambilan keputusan. Catatan: Hal ini meliputi pencarian informasi yang relevan, tepat waktu, dan akurat secara mendalam, di luar pertanyaan rutin atau lebih dari yang dituntut dalam pekerjaan.'),
('47', 'Orientasi terhadap Pelanggan', 'Keinginan dan usaha untuk membantu atau melayani pemenuhan kebutuhan pelanggan internal dan/atau eksternal organisasi, baik yang dinyatakan secara terbuka dan langsung maupun secara tidak langsung. Catatan: Fokus dari usaha di atas adalah mencari dan menemukan kebutuhan pelanggan, bukan sekedar mengenali secara umum pikiran, perasaan atau tingkah laku mereka'),
('48', 'Pemahaman Hubungan Antar Pribadi', 'Keinginan untuk menyimak dan mengerti secara akurat pikiran, perasaan, dan masalah orang lain, baik yang diucapkan maupun yang tidak terucapkan atau tidak sepenuhnya disampaikan. Catatan: Yang termasuk orang lain disini adalah individu atau sekelompok orang yang masing-masing anggotanya memiliki perasaan, minat dan perhatian yang sama. Kompetensi ini mengukur kompleksitas dan kedalaman pemahaman terhadap orang lain, juga termasuk kepekaan antar-budaya'),
('49', 'Dampak Dan Pengaruh', 'Keinginan untuk membujuk, meyakinkan, mempengaruhi, atau memberi kesan pada orang lain agar mereka mendukung rencananya. Catatan : Termasuk menekankan pada keinginan untuk mempengaruhi atau menimbulkan dampak pada orang lain.'),
('50', 'Pemahaman Keorganisasian', 'Kemampuan untuk memahami pola kekuasaan dan kewenangan dalam organisasi sendiri maupun organisasi lain (pelanggan, penyalur dll) Catatan: Ttermasuk kemampuan mengidentifikasi pengambil keputusan yang sebenarnya serta individu-individu yang memiliki pengaruh kuat di dalam organisasi yang termaksud'),
('51', 'Membangun Jejaring', 'Keinginan dan usaha untuk membangun atau membina jejaring yang berguna bagi tugas, pencapaian sasaran kerja serta peluang bisnis di masa depan, termasuk kontak di luar lingkup pekerjaan dan mengarah pada pertemuan sosial maupun pribadi'),
('52', 'Kerjasama', 'Keinginan untuk bekerjasama dengan orang lain secara aktif menjadi bagian dari kelompok dan memberikan kontribusi positif dalam penyelesaian tugas kelompok. Catatan: Kerjasama menekankan peran individu sebagai anggota kelompok, bukan bekerja secara terpisah atau saling berkompetisi bukan pula sebagai pemimpin. Kelompok di sini dalam arti yang luas, yaitu sekelompok individu yang menyelesaikan suatu tugas atau proses kerja secara bersama-sama'),
('53', 'Kepemimpinan', 'Keinginan untuk berperan dan bertindak sebagai pemimpin dalam suatu kelompok dan memastikan adanya kejelasan tujuan kerja serta arah organisasi di antara anggota kelompok. Catatan: Kepemimpinan umumnya muncul dari posisi otoritas formal, walaupun tidak harus selalu demikian. Kelompok juga dapat diartikan secara luas sebagai kelompok apapun dimana seseorang mengambil peran sebagai pemimpin.'),
('54', 'Mengembangkan Orang Lain', 'Keinginan yang tulus untuk mengajarkan atau mendorong orang lain untuk berkembang melalui proses pembelajaran formal atau informal. Catatan: Kompetensi ini fokus pada maksud/niat untuk mengembangkan orang lain bukan sekedar berdasarkan pada program pelatihan umum, tetapi melalui perumusan program pengembangan sebagai hasil pengamatan dan analisa terhadap kebutuhan masing-masing individu atau kelompok untuk suatu tujuan yang spesifik.'),
('55', 'Pengarahan', 'Keinginan untuk membuat orang lain mengikuti kemauannya dengan menggunakan kekuatan pribadi atau kewenangan jabatan secara efektif serta pada tempatnya, demi kepentingan jangka panjang organisasi. Catatan: Termasuk dalam perilaku mengarahkan adalah memberitahu orang lain apa yang harus dilakukan dengan nada yang bervariasi mulai dari meminta, tegas atau bahkan mengancam agar tugas-tugas dapat terselesaikan dengan baik'),
('56', 'Berpikir Konseptual', 'Kemampuan untuk mengendalikan emosi dan tingkahlaku negatif pada saat ditantang orang lain, dihadapkan pada perlawanan dan permusuhan dari pihak lain, atau ketika bekerja pada situasi di bawah tekanan'),
('57', 'Berpikir Analitis', 'Kemampuan untuk memahami situasi/masalah dengan cara menguraikannya menjadi bagian-bagian yang lebih kecil atau mengkaji sebab-akibat suatu situasi/masalah secara sistematis. Catatan: Termasuk mengorganisasikan bagian-bagian kecil secara sistimatis, membuat perbandingan, menetapkan prioritas, mengidentifikasikan urutan, mencari hubungan sebab akibat atau hubungan jika-maka'),
('58', 'Kepakaran', 'Mencakup kepakaran atas "job-related knowledge" (bisa berupa kemampuan teknis, profesional atau managerial), berikut motivasi untuk mengembangkan, menggunakan dan membagi pengetahuannya dengan orang lain.'),
('59', 'Fleksibilitas Terhadap Perubahan', 'Kemampuan untuk menyesuaikan diri dan bekerja secara efektif dengan berbagai situasi maupun individu, memahami dan menghargai perbedaan pendapat dan dengan mudah menerima perubahan didalam pekerjaan maupun organisasi'),
('60', 'Pengendalian Diri', 'Kemampuan untuk mengendalikan emosi dan tingkahlaku negatif pada saat ditantang orang lain, dihadapkan pada perlawanan dan permusuhan dari pihak lain, atau ketika bekerja pada situasi di bawah tekanan'),
('61', 'Kepercayaan Diri', 'Kemampuan mengambil tanggung jawab berdasarkan keyakinan akan kemampuan yang dimilikinya sehingga dapat mengambil keputusan dan dapat menghadapi kegagalan secara konstruktif/membangun.'),
('62', 'Komitmen Terhadap Organisasi', 'Kemampuan untuk memberikan prioritas kepada organisasi/perusahaan dengan bertindak menggunakan cara-cara yang membawa kepada sikap loyalitas serta integritas sebagai komitmen terhadap visi, misi dan nilai-nilai yang dianut perusahaan.');
INSERT IGNORE INTO competency_level (id_level, id_comp, level, deskripsi) VALUES
('1', '1', '1', 'Kemampuan menjelaskan informasi mengenai aspek-aspek remunerasi dengan baik. Contoh: menjelaskan benefit yang diberikan kepada karyawan.'),
('2', '1', '2', 'Kemampuan melaksanakan/menyelesaikan pekerjaan yang berhubungan dengan sistem remunerasi sesuai dengan prosedur yang berlaku. Contoh: melakukan perhitungan lembur dan atau melakukan up date database karyawan.'),
('3', '1', '3', 'Kemampuan mengelola sistem remunerasi dengan kualitas tinggi dari instruksi lisan atau tertulis, sehingga dapat berjalan sesuai dengan ketentuan. Contoh: melakukan pengelolaan medical benefit.'),
('4', '1', '4', 'Kemampuan menganalisa dan mengevaluasi sistem remunerasi untuk meningkatkan kesejahteraan karyawan tanpa bantuan atau instruksi untuk menghasilkan kinerja yang lebih baik. Contoh: melakukan annual salary review, mereview medical insurance.'),
('5', '1', '5', 'Kemampuan memformulasi/memodifikasi metoda pendekatan atau strategi kebijakan pada aspek remunerasi sebagai solusi untuk situasi bisnis yang kompleks. Contoh: merancang kebijakan employee benefit seperti reward system.'),
('6', '2', '1', 'Kemampuan menjelaskan ketentuan teknis dan prosedur H&S yang ada serta resiko/bahaya dalam suatu aktivitas. Contoh: menjelaskan secara umum prosedur dan resiko/bahaya yang ada dalam suatu aktivitas.'),
('7', '2', '2', 'Kemampuan melakukan,/melaksanakan tindakan pencegahan terhadap kemungkinan terjadinya kecelakaan dan kejadian lainnya termasuk kerusakan aset dalam suatu pekerjaan. Contoh: pemakaian alat pelindung diri (APD).'),
('8', '2', '3', 'Kemampuan mengelola resiko/bahaya yang mungkin timbul dalam suatu aktivitas pekerjaan serta tindakan pengendalian yang diperlukan agar tidak terjadi kecelakan atau kejadian lainnya termasuk kerusakan aset perusahaan sesuai ketentuan peraturan dan prosedur H&S yang ada. Contoh: mengelola pekerjaan yang mempunyai potensi resiko terjadinya kecelakaan kerja sesuai prosedur H&S.'),
('9', '2', '4', 'Kemampuan melakukan analisa/evaluasi terhadap ketentuan/prosedur H&S dan peralatan teknis H&S dalam implementasi pekerjaan di lapangan untuk menghasilkan pekerjaan yang lebih aman. Contoh: memberikan rekomendasi perbaikan prosedur dan peralatan H&S yang diperlukan untuk pekerjaan lapangan yang mempunyai potensi resiko terjadinya kecelakaan kerja.'),
('10', '2', '5', 'Kemampuan mengembangkan /memformulasi strategi teknis, pedoman atau prosedur H&S dengan mengacu pada peraturan dan UU H&S sebagai solusi untuk satu bisnis yang kompleks. Contoh: membuat kebijakan, prosedur, dan petunjuk teknis H&S.'),
('11', '3', '1', 'Kemampuan menjelaskan dasar dan tujuan penerapan Pengelolaan kinerja individu maupun organisasi yang meliputi pengukuran key performance indicator, pemantauan serta penilaian kinerja individu dan atau perusahaan. Contoh: menjelaskan konsep-konsep key performance indicator atau sasaran kerja yang harus dicapai.'),
('12', '3', '2', 'Kemampuan melaksanakan Pengelolaan kinerja individual dan organisasi sesuai dengan standar kerja yang ditetapkan. Contoh: melaksanakan penyusunan sasaran kerja individu.'),
('13', '3', '3', 'Kemampuan mengelola sasaran kerja baik individu dan organisasi untuk mencapai kinerja yang lebih baik. Contoh: mengintegrasikan dan menilai data kinerja dari seluruh unit.'),
('14', '3', '4', 'Kemampuan menganalisa dan mengevaluasi sistem dan pencapaian kinerja unit kerja terhadap organisasi tanpa bantuan atau instruksi. Contoh: memberikan rekomendasi sasaran kinerja setiap unit kerja berdasarkan sasaran kinerja perusahaan.'),
('15', '3', '5', 'Kemampuan memformulasikan/memodifikasi sistem dan prosedur Pengelolaan kinerja dengan pendekatan atau strategi yang komprehensif sebagai solusi untuk situasi bisnis yang kompleks. Contoh: memodifikasi sistem Pengelolaan kinerja di perusahaan.'),
('16', '4', '1', 'Kemampuan menjelaskan informasi mengenai Pengelolaan karir. Contoh: menjelaskan jenjang karir seorang karyawan.'),
('17', '4', '2', 'Kemampuan menjalankan proses identifikasi potensi jenjang karir karyawan sesuai dengan prosedur yang telah ditetapkan. Contoh: mengidentifikasi tingkat kompetensi masing-masing jabatan di unit kerjanya.'),
('18', '4', '3', 'Kemampuan mengelola sistem pengembangan karir untuk memenuhi kebutuhan sumberdaya manusia yang berkualitas tinggi. Contoh: melakukan pemetaan karyawan sesuai dengan potensi dan kompetensinya di HR System.'),
('19', '4', '4', 'Kemampuan menganalisa dan mengevaluasi pengembangan kompetensi karyawan berdasarkan Pengelolaan karir untuk mendapatkan hasil dengan kinerja yang lebih baik. Contoh: memberikan rekomendasi pengembangan kompetensi setiap unit kerja menjadi kompetensi perusahaan.'),
('20', '4', '5', 'Kemampuan memformulasikan/ memodifikasi pendekatan Pengelolaan karir sebagai solusi untuk situasi bisnis yang kompleks. Contoh: merancang Pengelolaan karir.'),
('21', '5', '1', 'Kemampuan menjelaskan informasi mengenai pengembangan organisasi. Contoh: menjelaskan kebutuhan karyawan berdasarkan struktur organisasi yang telah disetujui.'),
('22', '5', '2', 'Kemampuan melaksanakan fungsi-fungsi dalam organisasi yang telah ditetapkan oleh manajemen. Contoh: mengajukan usulan mutasi karyawan diinternal unit kerja.'),
('23', '5', '3', 'Kemampuan mengelola sistem pengembangan organisasi dengan kualitas tinggi dari instruksi lisan atau tertulis sehingga meningkatkan produktifitas perusahaan. Contoh: mengontrol kebijakan perusahaan.'),
('24', '5', '4', 'Kemampuan menganalisa dan mengevaluasi organisasi untuk mengetahui tingkat keberhasilan dalam penerapannya tanpa bantuan atau instruksi untuk menghasilkan kinerja yang lebih baik. Contoh: mereview efektifitas suatu kebijakan perusahaan di bidang HR.'),
('25', '5', '5', 'Kemampuan memformulasi/ memodifikasi metoda pendekatan atau strategi kebijakan pada aspek pengembangan organisasi sebagai solusi untuk situasi bisnis yang kompleks. Contoh: membuat usulan struktur organisasi yang efektif.'),
('26', '6', '1', 'Kemampuan menjelaskan mekanisme pelatihan dari identifikasi kebutuhan hingga proses evaluasi pelatihan sesuai dengan sasaran pelatihan. Contoh: menjelaskan mekanisme analisa kebutuhan pelatihan.'),
('27', '6', '2', 'Kemampuan merencanakan dan menjalankan pelatihan dari identifikasi kebutuhan hingga proses evaluasi pelaksanaan pelatihan sesuai dengan sasaran pelatihan. Contoh: menyusun modul pelatihan.'),
('28', '6', '3', 'Kemampuan mengelola pelatihan & pasca pelatihan sebagai proses pembelajaran terhadap individu untuk mencapai sasaran perusahaan dengan kualitas tinggi. Contoh: mengkoordinir pelaksanaan sharing/transfer knowledge.'),
('29', '6', '4', 'Kemampuan menganalisa dan mengevaluasi dampak dari pelatihan terhadap kinerja organisasi yang lebih baik. Contoh: menganalisa dampak hasil pelatihan terhadap organisasi.'),
('30', '6', '5', 'Kemampuan memformulasikan hasil dan memodifikasi metodologi pelatihan. Contoh: mendesain matriks pelatihan berdasarkan kompetensi.'),
('31', '7', '1', 'Mengetahui secara umum dan mampu menguraikan Standar Mutu untuk kelancaran pelaksanaan tugas (operasional). Contoh: dapat menjelaskan standar kualitas pekerjaan di area kerja masing-masing secara umum.'),
('32', '7', '2', 'Kemampuan untuk melaksanakan komponen Pengelolaan Mutu sesuai dengan bidang kerjanya. Contoh: operator di produksi melaksanakan pekerjaan sesuai dengan Instruksi Kerja dan dan mengisi form dan check list (sehingga menjadi catatan mutu).'),
('33', '7', '3', 'Kemampuan untuk mengelola data dari catatan mutu agar dapat memenuhi target dan persyaratan Pengelolaan Mutu. Contoh: mampu mengelola fungsi dalam area kerjanya sesuai dengan Prosedur Kerja dan Instruksi Kerja, untuk Produksi sesuai dengan ISO 9001-2000.'),
('34', '7', '4', 'Kemampuan untuk menganalisa dan mengevaluasi catatan mutu agar dapat menghasilkan mutu kinerja yang lebih baik tanpa bantuan atau instruksi. Contoh: mampu untuk merekomendasi perbaikan Prosedur, Instruksi Kerja, Form dan Chek List Pengelolaan Mutu Proses Produksi ISO 9001-2000.'),
('35', '7', '5', 'Kemampuan untuk memodifikasi komponen Pengelolaan Mutu Proses Operasional agar sesuai dengan kebutuhan bisnis dan regulasi (peraturan yang berlaku) serta perkembangan teknologi. Contoh: melakukan penyesuaian Kebijakan dan Pedoman Mutu agar sesuai dengan Business Plan Perusahaan dan Regulasi ISO 9001-2000.'),
('36', '8', '1', 'Memahami kalimat dan paragraf dalam aktivitas pekerjaan dan kemampuan untuk menjelaskan; kemampuan untuk mendengarkan dan memahami informasi dan usulan yang diberikan melalui kata dan kalimat baik lisan maupun tulisan. Contoh: staf pelaksana, dapat menerima dan memahami instruksi dari leader dan melakukan aktivitas sesuai instruksi.'),
('37', '8', '2', 'Kemampuan menggunakan kalimat dan paragraf serta mengkomunikasikan informasi dan usulan – baik secara lisan atau tulisan – sehingga orang lain menjadi paham (Kemampuan ekspresi oral/verbal, memahami dan mampu menjelaskan dalam kalimat kompleks). Contoh: mampu memberikan perintah dan penjelasan yang memadai untuk tugas tsb.'),
('38', '8', '3', 'Kemampuan mengelola komunikasi lisan dan tulisan, dapat menulis, membuat draft dan memahami tulisan serta mampu melakukan korespondensi secara memadai dalam Bahasa Indonesia formal, termasuk pemahaman tulisan (kemampuan untuk membaca dan memahami informasi dan usulan dalam bentuk tulisan) dan kemampuan ekspresi dalam tulisan (kemampuan mengkomunikasikan informasi dan usulan dalam tulisan sehingga orang lain menjadi paham). Contoh: mampu berkomunikasi formal baik secara lisan maupun tulisan/korespondensi maupun membuat laporan dan presentasi dengan bantuan Microsoft Office Program.'),
('39', '8', '4', 'Mampu melakukan analisa dan evaluasi terhadap semua bentuk komunikasi (kemampuan membaca apa yang tersirat dalam yang tersurat) termasuk kemampuan menerapkan aturan umum ke dalam problem yang khusus untuk menghasilkan jawaban yang memadai (metoda berpikir deduktif) dan kemampuan untuk mengkombinasikan sejumlah potongan informasi untuk menghasilkan konklusi ataupun aturan umum, termasuk menemukan keterkaitan di antara sejumlah kejadian yang tidak saling berhubungan (metoda berpikir induktif). Contoh: mampu membuat sekaligus mempresentasikan rencana/strategi kerja, analisa & evaluasi secara lisan maupun tulisan.'),
('40', '8', '5', 'Kemampuan untuk memformulasikan dan memodifikasi komunikasi (lisan dan tulisan) sebagai alat atau senjata untuk mencapai tujuan – termasuk kemampuan memberikan usulan yang cerdas ataupun tidak biasa atas suatu topik maupun situasi, atau kemampuan membangun cara yang kreatif untuk memecahkan suatu masalah dan kemampuan untuk memahami, memadukan dan mengatur informasi menjadi pola komunikasi yang berarti (kemampuan berbicara/menulis untuk membuat konklusi).  Contoh: mampu berargumentasi dalam persidangan dan membuat kontrak yang rumit.'),
('41', '9', '1', 'Kemampuan untuk menyampaikan pesan informasi secara akurat, tepat waktu dan mudah dimengerti secara verbal atau tertulis.  Contoh: menyampaikan pengumuman, membuat internal memo, membuat e-mail dst.'),
('42', '9', '2', 'Kemampuan untuk mengerti sudut pandang orang lain tentang suatu masalah /issue/problem dan mencari tahu pendapat, perasaan, concern yang tidak tersampaikan termasuk respon apa yang kira-kira akan diberikan. Contoh: read between the line.'),
('43', '9', '3', 'Kemampuan untuk mengerti lebih baik jauh sebuah situasi, mengidentifikasi kebutuhan komunikasi, menggunakan cara-cara yang berbeda, ragam media, dalam menyampaikan pesan untuk memperjelas pesan dan makna. Contoh: program kampanye “spot bill”.'),
('44', '9', '4', 'Kemampuan menganalisa dan mengevaluasi program/inisiatif/aktivitas komunikasi serta mampu melakukan monitoring dan penilaian untuk perbaikan kinerja program. Contoh: dampak dan performance dari subjek yang dikomunikasikan.'),
('45', '9', '5', 'Kemampuan memformulasi dan memodifikasi program komunikasi untuk memperoleh dukungan dari audiencenya. Contoh: Strategic Communication Plan.'),
('46', '10', '1', 'Kemampuan memahami dan menjelaskan dasar-dasar hukum dan prosedur dan kebijakan internal. Contoh: seluruh karyawan harus mengetahui peraturan perusahaan.'),
('47', '10', '2', 'Kemampuan melakukan pekerjaan dengan mematuhi rambu-rambu hukum dan peraturan yang berlaku seperti PERDA, hukum pidana, termasuk dasar-dasar hukum acara seperti membuat laporan polisi dan memperoleh perizinan. Contoh: mampu menjadi saksi di pengadilan , mengurus perizinan.'),
('48', '10', '3', 'Kemampuan mengelola pekerjaannya sesuai dengan rambu hukum dan peraturan yang berlaku (hukum pidana dan perdata, PERDA, dsb) yang berkaitan dengan kegiatan operasi perusahaan. Contoh: mampu memberi sanksi atas pelanggaran peraturan perusahaan, mampu melakukan investigasi/audit.'),
('49', '10', '4', 'Kemampuan menganalisa dan mengevaluasi hukum bisnis dan peraturan terkait lainnya termasuk: perburuhan, perusahaan, pasar modal, investasi, tanah dsb untuk membuat dokumen hukum atau tindakan hukum dalam memenuhi kebutuhan perusahaan. Kemampuan litigasi untuk menganalisa dan mengevaluasi posisi hukum perusahaan.  Contoh: mampu menganalisa peraturan perpajakan atau pasar modal.'),
('50', '10', '5', 'Kemampuan memformulasi dan memodifikasi tindakan hukum, dokumen hukum, nasehat hukum, prosedur dan kebijakan berdasarkan pemahaman hukum, teori, prinsip-prinsip dan filosofi hukum.  Contoh: mampu membuat peraturan perusahaan, mampu mewakili perusahaan di muka pengadilan.'),
('51', '11', '1', 'Kemampuan menjelaskan aktivitas operasional terkait Operasional Bisnis dalam area kerja pemegang jabatan. Contoh: mampu menjelaskan aktivitas terkait pembuatan neraca dan laporan laba rugi, sesuai urutan kerja.'),
('52', '11', '2', 'Kemampuan melaksanakan dan menyelesaikan proses aktivitas Operasional Bisnis di area kerja pemegang jabatan. Contoh: mampu melaksanakan kegiatan operasional sebagai akuntan.'),
('53', '11', '3', 'Kemampuan mengelola aktivitas bisnis di area kerja pemegang jabatan dengan standar kualitas yang ditetapkan. Contoh: mampu memonitor kualitas dan waktu pelaksanaan dan penyelesaian aktivitas absensi, evaluasi absensi dan alokasi sumberdaya terkait, mengelola penugasan shift.'),
('54', '11', '4', 'Kemampuan menganalisa dan mengevaluasi aktivitas bisnis di area kerja pemegang jabatan maupun unit kerja lain yang terkait dengan area kerja pemegang jabatan. Contoh: mampu mengevaluasi dan menganalisa aktivitas absensi dalam periode waktu tertentu, mengevaluasi kebutuhan dan pola penugasan operasional.'),
('55', '11', '5', 'Kemampuan memformulasikan atau melakukan modifikasi strategi pelaksanaan Bisnis di area kerja pemegang jabatan maupun unit kerja lain yang terkait dengan area kerja pemegang jabatan untuk mencapai standar kualitas yang lebih tinggi. Contoh: mampu memformulasi/ modifikasi strategi operasional sistem absensi dan diimplementasi dalam mekanisme & sistem operasional.'),
('56', '12', '1', 'Kemampuan menjelaskan aktivitas operasional terkait penanganan pelanggan (penerimaan keluhan, tindak lanjut penerimaan keluhan, informasi progress penyelesaian keluhan dalam database kepelangganan). Contoh: mampu menjelaskan kepada pelanggan tindak lanjut setelah menerima keluhan tagihan terlambat.'),
('57', '12', '2', 'Kemampuan melaksanakan dan menyelesaikan proses aktivitas operasional penanganan pelanggan yaitu penerimaan keluhan, tindak lanjut keluhan, update progress, penyelesaian keluhan, umpan balik, dan informasi penanganan dalam database kepelangganan. Contoh: mampu mencatatkan keluhan pelanggan tersebut dalam sistem, mampu menggunakan fitur sistem untuk memeriksa progress, menginput hasil akhir ke dalam sistem dan menelpon pelanggan mengenai hasil tindak lanjut.'),
('58', '12', '3', 'Kemampuan mengelola sejumlah aktivitas operasional penanganan pelanggan yang kompleks dengan standar kualitas yang ditetapkan. Contoh: mampu mengelola sejumlah aktivitas penyelesaian keluhan pelanggan.'),
('59', '12', '4', 'Kemampuan menganalisa dan mengevaluasi aktivitas operasional penanganan pelanggan yang kompleks dengan kualitas tinggi. Contoh: mampu mengevaluasi service level atas penyelesaian keluhan dan memastikan keluhan outstanding menjadi prioritas penyelesaian.'),
('60', '12', '5', 'Kemampuan memformulasikan atau melakukan modifikasi strategi penanganan pelanggan untuk meningkatkan kepuasan dan loyalitas pelanggan sesuai dengan target. Contoh: mampu membuat solusi untuk masalah keluhan yang kompleks sehingga bisa diselesaikan dengan lebih cepat dan tepat.'),
('61', '13', '1', 'Kemampuan menjelaskan fungsi Akun Kepelangganan dan standar/prosedur kerja, standar pelayanan dan kebijakan yang berlaku. Contoh: mampu menjelaskan kepada pelanggan baru prosedur kepelangganan baru, waktu penyelesaiannya dan biaya yang dibutuhkan.'),
('62', '13', '2', 'Kemampuan melaksanakan dan menyelesaikan aktivitas Akun Kepelangganan sesuai dengan standar/prosedur kerja, standar pelayanan dan kebijakan yang berlaku. Contoh: mampu melakukan survey atau perhitungan biaya atau memproses permohonan pelanggan baru.'),
('63', '13', '3', 'Kemampuan mengelola aktivitas Akun Kepelangganan dengan standar kualitas yang ditetapkan. Contoh: mampu mengelola proses pelanggan baru (sejak penerimaan data, monitoring survey, penetapan tarif sampai dengan implementasi/delivery) sesuai standar waktu yang ditetapkan.'),
('64', '13', '4', 'Kemampuan menganalisa dan mengevaluasi aktivitas Akun Kepelangganan. Contoh: mampu menganalisa dan melakukan evaluasi potensi peningkatan penjualan produk/jasa di segmen pasar tertentu tetapi banyak ditemukan pelanggan dengan pembelian produk/penggunaan jasa bervolume kecil.'),
('65', '13', '5', 'Kemampuan memformulasikan atau melakukan modifikasi aktivitas Akun Kepelangganan untuk mencapai standar kualitas yang lebih baik. Contoh: mampu menyusun formulasi/modifikasi strategi peningkatan pelayanan pelanggan Key Account.'),
('66', '14', '1', 'Kemampuan menjelaskan aktivitas terkait Pemasaran secara sederhana. Contoh: mampu menjelaskan aktivitas promosi pembagian sampel produk di pusat perbelanjaan.'),
('67', '14', '2', 'Kemampuan melaksanakan aktivitas Pemasaran sesuai prosedur/ pola Pemasaran yang telah disepakati. Contoh: mampu menyiapkan, mengurus perijinan pendistribusian brosur promosi penjualan produk di area tertentu sesuai perencanaan yang telah ditentukan.'),
('68', '14', '3', 'Kemampuan mengelola aktivitas Pemasaran yang telah di sepakati dan sesuai standar kualitas yang ditetapkan. Contoh: mampu mengelola sumber daya untuk beberapa aktivitas Pemasaran secara bersamaan dan memenuhi tenggat waktu yang ditetapkan.'),
('69', '14', '4', 'Kemampuan menganalisa dan mengevaluasi aktivitas Pemasaran yang telah disepakati dan mengukur hasil aktivitas Pemasaran tersebut terhadap target yang ditetapkan. Contoh: mampu menganalisa efektivitas promosi penjualan produk atau analisa efektivitas promosi dengan pembagian sampel produk di satu area.'),
('70', '14', '5', 'Kemampuan memformulasikan atau melakukan modifikasi konsep, rencana bisnis, strategi dan implementasi terkait Pemasaran. Contoh: mampu memformulasikan metoda baru dalam promosi/pemasaran sehingga lebih efektif dan meningkatkan kualitas dan implementasi/delivery bagi pelanggan baru secara signifikan.'),
('71', '15', '1', 'Kemampuan untuk memahami dan menguraikan informasi eksternal dan internal yang dapat berdampak pada arah, rencana, dan performance umum perusahaan. Contoh: Info Market, Faktor Ekonomi, PKS.'),
('72', '15', '2', 'Kemampuan memanfaatkan informasi eksternal dan internal untuk pembuatan modeling/ perhitungan yang dapat dijadikan masukan dalam penetapan arah, rencana, serta performance perusahaan. Contoh: mampu mengembangkan Modelling Kebutuhan Supply, Demand, Revenue, dll.'),
('73', '15', '3', 'Kemampuan untuk mengelola, memperbaiki dan menggabungkan perhitungan-perhitungan/ modeling yang dapat dijadikan sebagai dasar penetapan pilihan/ opsi arah, rencana serta performance perusahaan sesuai instruksi. Contoh: mampu menyusun Strategic Planning (Combining Model untuk Opsi Pengembangan/Rehab Jaringan, Usulan Capex per fungsi).'),
('74', '15', '4', 'Kemampuan untuk menganalisa dan mengevaluasi pilihan atau opsi yang berdasar pada Informasi eksternal dan internal yang dapat berpengaruh pada arah, rencana, strategi baru dan performance internal (termasuk performance terkini) untuk menghasilkan kinerja yang lebih baik tanpa bantuan atau instruksi. Contoh: mampu menyusun Strategic Management (Opsi untuk Master Plan, Forecasting - Re-Forecasting, Prioritas Usulan capex).'),
('75', '15', '5', 'Kemampuan untuk memformulasikan/ memodifikasi arah, rencana, strategi baru, mendesign ulang binis proses berdasarkan kondisi eksternal (peraturan pemerintah, PKS, makro ekonomi, dll) dan performance internal sebagai solusi untuk situasi bisnis yang kompleks. Contoh: mampu menyetujui Corporate Plan, membangun Corporate Culture, Corporate Values, Share Selling, Rebasing.'),
('76', '16', '1', 'Kemampuan untuk menjelaskan komponen dan fungsi dari asset manajemen. Contoh: mengetahui jenis fixed asset (kendaraan operasional, komputer).'),
('77', '16', '2', 'Kemampuan untuk mengidentifikasi semua jenis fixed asset yang dimiliki berkaitan dengan kegiatan operasional. Contoh: melakukan registrasi dan klasifikasi kendaraan operasional.'),
('78', '16', '3', 'Kemampuan mengelola data dan informasi dikaitkan dengan kinerja fixed asset. Contoh: mengukur kinerja fixed asset.'),
('79', '16', '4', 'Kemampuan melakukan analisis dan evaluasi kinerja bagi keperluan pengembangan fixed asset. Contoh: penyiapan rencana pengembangan fixed asset.'),
('80', '16', '5', 'Kemampuan memodifikasi dan memformulasikan kembali strategi kebutuhan fixed asset terkait pengembangan operasional dan investasi. Contoh: merumuskan strategi pengembangan fixed asset.'),
('81', '17', '1', 'Kemampuan menjelaskan definisi Pengelolaan proyek secara benar dan tepat. Contoh: menjelaskan komponen Pengelolaan proyek.'),
('82', '17', '2', 'Kemampuan melaksanakan aktivitas Pengelolaan proyek. Contoh: menyiapkan scheduling proyek, project, critical path, melakukan supervisi.'),
('83', '17', '3', 'Kemampuan mengelola suatu Pengelolaan proyek dengan baik dan benar sesuai prosedur. Contoh: melakukan kontrol progress proyek.'),
('84', '17', '4', 'Kemampuan melakukan analisis dan evaluasi pelaksanaan dan kinerja Pengelolaan proyek. Contoh: memberikan rekomendasi perbaikan pengelolaan proyek.'),
('85', '17', '5', 'Kemampuan memformulasikan dan melakukan penyempurnaan strategi pelaksanaan dan pengelolaan Pengelolaan proyek untuk mencapai sasaran proyek. Contoh: menetapkan perencanaan pelaksanaan proyek.'),
('86', '18', '1', 'Kemampuan memahami dan dapat menjelaskan posisi, keberpihakan ataupun perbedaannya (pendapat, sikap, tindakan, perilaku, dsb) dengan pihak lain. Contoh: kemampuan menjelaskan pendapat dan mendengarkan pendapat orang lain dalam rapat.'),
('87', '18', '2', 'Mampu melakukan negosiasi sederhana dengan kemampuan memahami perbedaan dan reaksi pihak lain (Social perceptiveness). Contoh: kemampuan bermusyawarah dalam rapat dengan warga/pelanggan saat temu pelanggan di kelurahan, mampu menyelesaikan keluhan pelanggan di Contact Center.'),
('88', '18', '3', 'Kemampuan untuk mengelola perbedaan (pendapat, sikap, tindakan, perilaku, dsb) dengan pihak lain untuk memperoleh alternatif solusi dan mampu melakukan pendekatan (sosiologis dan antropologis) agar pihak lain menerima solusi yang diajukan. Contoh: kemampuan menghadapi dan memberi solusi terhadap keluhan pelanggan.'),
('89', '18', '4', 'Kemampuan menganalisa dan mengevaluasi permasalahan (complex problem solving) dan perbedaan yang terjadi sehingga dapat mempengaruhi pihak lain agar mengubah pendirian mereka dan mereka menerima solusi yang diajukan. Contoh: kemampuan negosiasi kontrak pembelian bahan-bahan baku produksi.'),
('90', '18', '5', 'Kemampuan memformulasi dan memodifikasi strategi guna mendapatkan solusi terbaik dalam situasi sesulit apapun untuk menyatukan pendapat atau kepentingan yang berbeda dengan mempertimbangkan untung dan rugi atas tindakan yang dilakukan (kemampuan penilaian dan pengambilan keputusan).  Contoh: kemampuan menyelesaikan sengketa yang rumit melalui cara negosiasi tanpa melalui jalur pengadilan, seperti penyelesaian perselisihan Perusahaan Lain.'),
('91', '19', '1', 'Kemampuan menjelaskan informasi dan proses penerbitan tagihan yang ada di billing system. Contoh: mampu menjelaskan isi tagihan.'),
('92', '19', '2', 'Kemampuan melaksanakan/menyelesaikan pekerjaan berhubungan dengan data tagihan dan pelanggan di billing system dan mengelola database/arsip billing serta me”record” dalam sistem. Contoh: mampu melakukan mutasi data tagihan atau mampu mencetak tagihan atau mampu menangani keluhan tagihan atau mampu mengirim “bill file” ke “eksternal payment point” atau mampu melakukan “posting cash file”, mampu melakukan pengarsipan dokumen perubahan data.'),
('93', '19', '3', 'Kemampuan mengelola dan merekonsiliasi data tagihan (billing) dan pelanggan yang berhubungan dengan proses penerbitan tagihan yang akurat sesuai dengan SOP yang berlaku. Contoh: mampu merekonsiliasi data tagihan, mampu mengelola aktivitas di level 2, mampu mengalokasi pembayaran.'),
('94', '19', '4', 'Kemampuan menganalisa, mengevaluasi dan mengolah informasi yang berhubungan dengan tagihan dan pelanggan untuk menghasilkan tagihan dan data kepelangganan dengan kualitas tinggi. Contoh: mampu menganalisa laporan penagihan, pembuatan master cetak-master bayar, mampu membuat laporan dari/ke sistem billing dgn mitra luar perusahaan, mampu menganalisa efektifitas internal/eksternal payment point.'),
('95', '19', '5', 'Kemampuan memformulasi/memodifikasi strategi proses penagihan, informasi kepelangganan dan pemeliharaan data sebagai solusi untuk situasi bisnis yang kompleks dan dampaknya terhadap kinerja proses bisnis yang dilayani. Contoh: mampu menyusun Strategi penerbitan tagihan, menyusun strategi “payment point”, menyusun kontrak dengan pihak ketiga yang berkaitan dengan tagihan, strategi penyimpanan “data base” tagihan dan pelanggan.'),
('96', '20', '1', 'Kemampuan menjelaskan kebijakan dan proses penagihan secara umum. Contoh: mampu memberikan informasi proses penagihan pada saat pemutusan kepelangganan.'),
('97', '20', '2', 'Kemampuan melaksanakan/menyelesaikan proses penagihan (non teknis). Contoh: mampu meyakinkan pelanggan masalah tagihan sesuai dengan pemakaian yang sebenarnya, mampu menyiapkan data untuk aktivitas penagihan.'),
('98', '20', '3', 'Kemampuan mengelola informasi atau data penagihan untuk menghasilkan skala prioritas penagihan yang akurat. Contoh: mampu mengelola data untuk menentukan proses kepelangganan selanjutnya, mampu membuat profil tunggakan.'),
('99', '20', '4', 'Kemampuan menganalisa, mengevaluasi serta mengolah data penagihan untuk menghasilkan kualitas yang lebih tinggi. Contoh: mampu menentukan banyaknya cicilan, mampu menentukan efektifitas penagihan dan pemutusan sementara kepelangganan, mampu membuat laporan dengan database yang kompleks.'),
('100', '20', '5', 'Kemampuan memformulasi/ memodifikasi strategi penagihan dan pengelolaan arsip yang dapat memberi nilai tambah kepada perusahaan dengan mempertimbangkan kemajuan teknologi dan dampaknya terhadap bisnis. Contoh: menentukan strategi penagihan melalui pemutusan sementara kepelangganan.'),
('101', '21', '1', 'Kemampuan menjelaskan informasi yang berkaitan dengan seluruh transaksi dan kustodian (penyimpanan surat berharga, seperti : BPKB, Bank Garansi, certificate time deposit, polis asuransi) untuk keperluan pembukuan , pelaporan dan batasan-batasan (SOP) yang ditentukan perusahaan. Contoh: menjelaskan prosedur seluruh transaksi untuk keperluan pembukuan disetiap bisnis unit.'),
('102', '21', '2', 'Kemampuan melaksanakan kegiatan transaksi dan rekonsiliasi uang kas masuk/keluar serta penyimpanan dokumen berharga perusahaan dari seluruh bisnis perusahaan. Contoh: melakukan kegiatan penerimaan dan rekonsiliasi kas pada kantor pelayanan.'),
('103', '21', '3', 'Kemampuan mengelola arus kas, surat berharga dan pencatatan seluruh transaksi sesuai SOP. Contoh: mengelola seluruh transaksi dengan menggunakan data/informasi dari setiap unit bisnis ataupun sistem keuangan.'),
('104', '21', '4', 'Kemampuan mengevaluasi arus kas perusahaan atas prosedur capex, operational expenses dan kewajiban perusahaan, sesuai kebutuhan perusahaan. Contoh: mengevaluasi keuangan perusahaan sehingga terhindarkan dari kemungkinan kerugian kurs, kelebihan bayar, kekurangan pembayaran ataupun terjadinya idle cash.'),
('105', '21', '5', 'Kemampuan untuk memodifikasi standard cash flow perusahaan sesuai dengan strategi perusahaan dengan mengacu pada standard pelaporan keuangan dan seluruh kewajiban perusahaan sehingga dapat membuat cash menjadi lebih efisien. Contoh: memodifikasi standar cash flow sesuai dengan perubahan kontrak kerjasama, pengaruh makro-ekonomi, ataupun perubahan perundangan2 an pada pasar modal sehingga tidak ada dana cash yang tidak produktif.'),
('106', '22', '1', 'Kemampuan menjelaskan jenis-jenis dokumen transaksi, cara memasukkan data ke sistem . Contoh: memasukkan data dari dokumen asal ke modul Buku Besar, Piutang, Hutang, Order pembelian, Faktur penjualan.'),
('107', '22', '2', 'Kemampuan untuk melaksanakan pembukuan dan akurasi pencatatan dokumen. Contoh: memberi nomor dan deskripsi akun atas jurnal pembelian barang, serta mencek kebenaran pencatatan datanya di komputer pemroses data.'),
('108', '22', '3', 'Kemampuan untuk mengelola Buku Besar, menyusun laporan keuangan dasar ( Laporan Laba Rugi, Laporan Neraca serta Laporan Arus Kas) secara periodik, berdasarkan prosedur akuntansi yang ditetapkan perusahaan. Contoh: menghasilkan Neraca Percobaan, Buku Besar bulanan dan rekonsiliasinya, dilanjutkan untuk menghasilkan Laporan Laba Rugi, Neraca, Arus Kas.'),
('109', '22', '4', 'Kemampuan mengevaluasi akun-akun di dalam laporan keuangan, kontrol internal prosedur yang lazim diterapkan oleh akuntansi. dan mengusulkan prosedur akuntansi manual yang akan digunakan. serta menyusun laporan keuangan umum maupun khusus. Contoh: laporan Keuangan khusus untuk memenuhi Per UU an otoritas pasar modal. Laporan Keuangan Umum yang menjadi subyek audit oleh akuntan publik sebelum diterbitkan.'),
('110', '22', '5', 'Kemampuan untuk memformulasi, memodifikasi, menerapkan kebijakan dan kinerja keuangan yang efektif dan efisien. Contoh: menyusun kebijakan mengenai penanganan piutang perusahaan berdasarkan rasio.'),
('111', '23', '1', 'Kemampuan menjelaskan tujuan, keperluan anggaran biaya dan pelaksanaan kontrol anggaran sesuai dengan target perusahaan pada periode yang berlaku. Contoh: memberikan format data anggaran yang diperlukan dari tiap unit dan tenggat waktu pengiriman kembali.'),
('112', '23', '2', 'Kemampuan melaksanakan pengisian data anggaran yang ditetapkan pada format pengisian usul anggaran,dan melaksanakan laporan aktual aktivitas terhadap anggaran yang telah disetujui. Contoh: membuat anggaran biaya umum dari Direktorat Customer Service yang berasal dari anggaran tiap departemen yang ada.'),
('113', '23', '3', 'Kemampuan mengelola dan mengontrol anggaran sesuai dengan target perusahaan. Contoh: membuat penyesuaian pada laporan usulan anggaran untuk efisiensi biaya. Melakukan pengecekan saldo anggaran untuk tiap usulan aktivitas di Direktoratnya.'),
('114', '23', '4', 'Kemampuan menganalisa dan mengevaluasi usulan anggaran, berdasar realisasi hasil tahun sebelumnya dan rencana perusahaan jangka panjang, asumsi ekonomi makro serta keadaan sosial politik saat berlaku, bekerja sama dengan unit kerja terkait untuk menyesuaikan permintaan setiap jenis anggaran dengan target dan strategi keuangan perusahaan dan laporan penyimpangan positif maupun negatif atas pelaksanaan aktual anggaran. Contoh: mencari alternatif pengadaan bahan baku produksi tertentu yang mengalami peningkatan harga melebihi anggaran.'),
('115', '23', '5', 'Kemampuan memformulasi dan memodifikasi anggaran, kebijakan perusahaan yang terpadu dan terkoordinasi, antara anggaran penjualan, biaya langsung maupun biaya umum, anggaran kas, anggaran pengeluaran biaya modal menjadi anggaran laba rugi serta neraca dan arus kas perusahaan. Contoh: menyajikan anggaran perusahaan tahunan yang disusun oleh Direktorat Keuangan.'),
('116', '24', '1', 'Kemampuan menjelaskan peraturan Pajak yang berlaku bagi perusahaan. Contoh: menjelaskan perlunya NPWP bagi perseorangan serta cara mendapatkannya, tarif pajak yang berlaku.'),
('117', '24', '2', 'Kemampuan melaksanakan perhitungan pajak terhutang berdasar transaksi bisnis yang sesuai dengan UU Pajak berlaku. (PPN, PPh, PBB, Materai) Contoh: menghitung pph pasal 21, pasal 23/26 untuk setiap transaksi keuangan.'),
('118', '24', '3', 'Kemampuan mengelola laporan perpajakan bulanan dan tahunan berdasarkan ketentuan UU Pajak yang berasal dari data transaksi yang merupakan objek pajak, dan melakukan rekonsiliasi terhadap pencatatan di Buku Besar. Contoh: menyajikan rekonsiliasi tahunan laporan PPH pasal 21, Pasal 23/26, PPN.'),
('119', '24', '4', 'Kemampuan menganalisa dan mengevaluasi laporan pajak perusahaan, efek UU Perpajakan terhadap kegiatan operasional perusahaan yang aktual maupun rencana, seperti dampak pajak pada analisa pengeluaran modal/investasi. Contoh: menyajikan presentasi perhitungan tahunan pajak penghasilan perusahaan, beserta pajak yang ditangguhkan.'),
('120', '24', '5', 'Kemampuan memformulasi dan memodifikasi strategi perpajakan yang sesuai dengan UU Pajak untuk memberikan manfaat maksimal kepada perusahaan. Contoh: menetapkan tunjangan makan/transport/seragam dll. sebagai tambahan penghasilan (PPh pasal 21), daripada memberikan makan siang/transport/seragam dll ( in natura) kepada pegawai, karena tingkat pajak rata-rata perorangan lebih rendah daripada tingkat pajak perusahaan.'),
('121', '25', '1', 'Kemampuan menjelaskan tujuan suatu usulan pengeluaran modal. Contoh: menjelaskan tujuan pembangunan perluasan jaringan pipa.'),
('122', '25', '2', 'Kemampuan melaksanakan perhitungan pengeluaran modal investasi. Contoh: melakukan perhitungan tentang masa guna investasi, arus kas serta pajak yang mempengaruhinya, dan tingkat diskonto, guna penghitungan Nilai Sekarang Bersih.'),
('123', '25', '3', 'Kemampuan mengkoordinasikan data usulan investasi beserta resikonya dengan memperhitungkan faktor-faktor yang menunjang kelayakannya. Contoh: menyajikan perbandingan kelayakan investasi antara metode Nilai Sekarang Bersih dengan metode Pengembalian Hasil Intern.'),
('124', '25', '4', 'Kemampuan menganalisa dan mengevaluasi perhitungan kelayakan usulan investasi, serta menambahkan analisa kualitatif untuk menghasilkan kinerja yang lebih baik. Contoh: menyajikan analisa kekuatan dan kelemahan faktor-faktor lain yang mendukung (misalnya : Pengadaan barang modal, peraturan pemerintah di bidang investasi, integritas kontraktor di pasar).'),
('125', '25', '5', 'Kemampuan memformulasi/ memodifikasi metode penilaian kelayakan investasi sebagai solusi untuk situasi bisnis yang kompleks. Contoh: menetapkan model investasi project financing.'),
('126', '26', '1', 'Kemampuan untuk menjelaskan informasi yang berkaitan dengan konsep dasar Pengelolaan resiko. Contoh: menjelaskan kemungkinan resiko yang akan dihadapi perusahaan berkaitan dengan operasional perusahaan.'),
('127', '26', '2', 'Kemampuan untuk mengidentifikasi kemungkinan terjadinya dampak resiko di setiap unit bisnis perusahaan. Contoh: kemampuan untuk mengidentifikasi kemungkinan bahwa perusahaan akan terkena pengaruh yang signifikan di pasar uang atas perkembangan makro ekonomi.'),
('128', '26', '3', 'Kemampuan mengukur tingkat resiko yang mempunyai dampak terhadap perusahaan. Contoh: mengukur tingkat resiko atas fluktuasi kurs terhadap pengadaan barang.'),
('129', '26', '4', 'Kemampuan untuk menganalisa dan mengevaluasi pengaruh resiko yang menyebabkan gangguan operasional perusahaan. Contoh: mengevaluasi peralatan produksi/barang produksi/barang support untuk menghindarkan kemungkinan terhambatnya operasi perusahaan.'),
('130', '26', '5', 'Kemampuan untuk merancang strategi yang tepat dalam penanganan resiko. Contoh: rancangan Business Continuity Plan.'),
('131', '27', '1', 'Kemampuan untuk memahami secara umum dasar-dasar audit sesuai dengan fungsi-fungsi yang ada di perusahaan. Contoh: dapat menjelaskan pengertian dan bentuk audit IT.'),
('132', '27', '2', 'Kemampuan melaksanakan audit sesuai dengan fungsi-fungsi yang ada di perusahaan. Contoh: melaksanakan audit keuangan.'),
('133', '27', '3', 'Kemampuan mengkoordinasikan kegiatan audit sesuai dengan fungsi-fungsi yang ada di perusahaan dan menyusun laporan audit sesuai standard. Contoh: mengkoordinasikan pelaksanaan audit operasional dan menyusun laporan.'),
('134', '27', '4', 'Kemampuan menganalisa kegiatan dan laporan audit sesuai dengan fungsi-fungsi yang ada di perusahaan dan audit yang bersifat khusus yang mengindikasikan penyimpangan, serta mengusulkan perbaikan secara menyeluruh terhadap temuan audit. Contoh: mengawasi dan mengendalikan pelaksanaan audit khusus serta mengindikasikan pelakunya'),
('135', '27', '5', 'Kemampuan mengembangkan dan memodifikasi metode, prosedur dan tata kerja rencana audit atau perbaikan pedoman kerja audit sesuai dengan fungsi-fungsi yang ada di perusahaan serta memberikan saran rekomendasi perbaikan untuk proses good corporate governance. Contoh: mengembangkan prosedur pelaksanaan audit untuk mendapatkan data dan fakta secara teintegrasi.'),
('136', '28', '1', 'Kemampuan menjelaskan proses pengadaan, baik barang maupun jasa, yang dibutuhkan untuk mendukung kelangsungan operasional perusahaan. Contoh: mampu menjelaskan proses pengadaan sesuai dengan SOP.'),
('137', '28', '2', 'Kemampuan melakukan proses pengadaan yang dibutuhkan untuk mendukung operasional perusahaan. Contoh: mampu melakukan pengadaan untuk kebutuhan sehari-hari sesuai dengan SOP, dari terima permintaan, melihat kontrak, menuangkan dalam purchase order/work order.'),
('138', '28', '3', 'Kemampuan mengelola proses pengadaan dengan QCSD level yg tinggi dari instruksi lisan atau tertulis untuk mendukung operasional perusahaan.Contoh: mampu mengelola pengadaan untuk barang atau jasa utama dengan spesifikasi khusus yang membutuhkan survey, estimasi, bidding, sourcing, technical meeting dan merekomendasikan pemasok dengan berdasar QCSD.'),
('139', '28', '4', 'Kemampuan menganalisa dan mengevaluasi proses pengadaan tanpa instruksi dan atau bantuan untuk menghasilkan kinerja yang lebih baik Contoh: menganalisa dan mengevaluasi efektifitas dan efisiensi pengadaan (dengan parameter QCSD) dan membuat perbaikan proses.'),
('140', '28', '5', 'Kemampuan memformulasi/memodifikasi pendekatan atau strategi proses pengadaan sebagai solusi untuk situasi bisnis perusahaan yang semakin kompleks. Contoh: membuat strategi pengadaan dengan supply positioning model berdasar bisnis perusahaan dan kondisi pasar.'),
('141', '29', '1', 'Kemampuan menjelaskan proses perencanaan kebutuhan dan pengendalian persediaan barang untuk mendukung operasional perusahaan. Contoh: menjelaskan parameter-parameter dalam perencanaan kebutuhan barang dan formulasinya.'),
('142', '29', '2', 'Kemampuan melakukan proses perencanaan kebutuhan dan pengendalian persediaan barang untuk mendukung operasional perusahaan. Contoh: mampu melakukan pengumpulan data dan melakukan perencanaan berdasar formula dan data yang tersedia.'),
('143', '29', '3', 'Kemampuan mengelola proses perencanaan kebutuhan dan pengendalian persediaan barang dengan kualitas yang tinggi dari instruksi lisan atau tertulis untuk mendukung operasional perusahaan. Contoh: mengelola proses perencanaan berdasar formula dan data yang tersedia serta outlook kebutuhan dari pihak terkait (dan melakukan adjustment jika diperlukan).'),
('144', '29', '4', 'Kemampuan menganalisa dan mengevaluasi proses perencanaan kebutuhan dan pengendalian persediaan barang tanpa intruksi dan atau bantuan untuk menghasilkan kinerja yang lebih baik. Contoh: melakukan evaluasi akurasi dari tingkat perencanaan kebutuhan berdasar formula yang digunakan (dan adjustment yang dilakukan) dan rencana perbaikan (baik internal maupun external domain).'),
('145', '29', '5', 'Kemampuan memformulasi/memodifikasi pendekatan atau strategi proses perencanaan kebutuhan dan pengendalian persediaan barang sebagai solusi untuk situasi bisnis perusahaan yang semakin kompleks. Contoh: melakukan evaluasi terhadap formula yang digunakan dan strategi perencanaan kebutuhan sesuai dengan bisnis perusahaan dan kondisi pasar.'),
('146', '30', '1', 'Kemampuan menjelaskan kegiatan logistik yang dibutuhkan untuk mendukung operasional perusahaan sesuai SOP. Contoh: menjelaskan konsep logistik dan implementasi di perusahaan.'),
('147', '30', '2', 'Kemampuan melakukan kegiatan logistik yang dibutuhkan untuk mendukung operasional perusahaan. Contoh: melakukan aktifitas pergudangan dan distribusi barang berdasar konsep logistik di perusahaan.'),
('148', '30', '3', 'Kemampuan mengelola kegiatan logistik dengan kualitas yang tinggi dari instruksi lisan atau tertulis untuk mendukung operasional perusahaan. Contoh: mengelola aktifitas logistik dan interaksinya dengan pihak-pihak internal dan eksternal.'),
('149', '30', '4', 'Kemampuan menganalisa dan mengevaluasi kegiatan logistik tanpa intruksi dan atau bantuan untuk menghasilkan kinerja yang lebih baik. Contoh: mengevaluasi aktifitas logistik dan membuat rencana perbaikan untuk mendapatkan value yang lebih (termasuk di dalamnya kondisi warehouse dan transportation).'),
('150', '30', '5', 'Kemampuan memformulasi/memodifikasi pendekatan atau strategi proses logistic sebagai solusi untuk situasi bisnis perusahaan yang semakin kompleks. Contoh: membuat strategi logistik yang tepat (misalnya desentralisasi, sentralisasi, lay-out, alur barang).'),
('151', '31', '1', 'Kemampuan menjelaskan sistem pengamanan terhadap aset-aset perusahaan. Contoh: menjelaskan sistem pengamanan di Kantor Pelayanan Pelanggan.'),
('152', '31', '2', 'Kemampuan melaksanakan pengamanan asset perusahaan sesuai SOP. Contoh: melaksanakan tugas-tugas khusus di Kantor Pelayanan Pelanggan.'),
('153', '31', '3', 'Kemampuan mengelola tugas-tugas pengamanan aset perusahaan dengan kualitas tinggi berdasarkan instruksi lisan atau tertulis.  Contoh: menangani kasus2 darurat dan cepat tanggap, pengawalan aktivitas pemutusan sementara atau permanen, penyiapan laporan, penanganan tagihan.'),
('154', '31', '4', 'Kemampuan menganalisa dan mengevaluasi pelaksanaan tugas pengamanan perusahaan tanpa bantuan atau instruksi untuk menghasilkan kinerja yang lebih baik. Contoh: memantau kinerja sekuriti melalui umpan balik dari pihak-pihak terkait dan melaksanakan tindak-lanjut yang diperlukan, termasuk memberikan masukan kepada Pengelolaan mengenai hal-hal bersifat rahasia, sebagai bahan pengambilan keputusan.'),
('155', '31', '5', 'Kemampuan memformulasi dan memodifikasi pendekatan atau strategi keamanan sebagai solusi untuk mengatasi situasi bisnis perusahaan yang kompleks. Contoh: memodifikasi sistem sekuriti yang disesuaikan dengan kebutuhan dan capex perusahaan, dikaitkan dengan kemajuan teknologi terkait.'),
('156', '32', '1', 'Kemampuan menjelaskan tugas-tugas dan tanggungjawab utama berkaitan dengan transportasi di seluruh lokasi perusahaan. Contoh: menjelaskan prosedur standar yang mengacu pada peraturan perusahaan, dalam pelaksanaan tugas-tugas transportasi'),
('157', '32', '2', 'Kemampuan melaksanakan/menyelesaikan pekerjaan berhubungan dengan transportasi, meliputi: operasional sehari-hari, administrasi, keuangan, perawatan dan perbaikan, dll. Contoh: menyiapkan kendaraan sesuai kebutuhan pemohon dan standar K3.'),
('158', '32', '3', 'Kemampuan mengelola transportasi perusahaan dengan kualitas tinggi merujuk kepada KPI dan instruksi lisan atau tertulis. Contoh: memenuhi kebutuhan operasional melalui langkah terobosan pada saat darurat.'),
('159', '32', '4', 'Kemampuan menganalisa dan mengevaluasi kinerja transportasi tanpa bantuan atau instruksi untuk menghasilkan kinerja yang lebih baik berbasis efektivitas dan efisiensi. Contoh: membuat laporan pencapaian kinerja bulanan disertai catatan-catatan jika ada kondisi khusus dan merekomendasikan atau membuat keputusan jika diperlukan tindakan khusus.'),
('160', '32', '5', 'Kemampuan memformulasi dan memodifikasi pendekatan atau strategi penyediaan sarana transportasi sebagai solusi untuk mengatasi bisnis perusahaan yang kompleks. Contoh: menganalisis biaya vs manfaat dan menetapkan perubahan kebijakan dan Prosedur yang bertujuan pemenuhan kebutuhan secara efektif dan efisien, sesuai kondisi perusahaan.'),
('161', '33', '1', 'Kemampuan menjelaskan tugas-tugas dan tanggungjawab pengelolaan perkantoran di seluruh lokasi perusahaan, meliputi: standardisasi ruang kerja, administrasi, kelistrikan, tugas-tugas kesekretariatan, resepsionis, perjalanan dinas, perawatan dan perbaikan gedung perkantoran serta kelengkapannya, jasa kebersihan, office boy dan pertamanan. Contoh: menjelaskan cara pemupukan dan pemangkasan tanaman.'),
('162', '33', '2', 'Kemampuan melaksanakan/menyelesaikan pekerjaan berhubungan dengan pengelolaan perkantoran. Contoh: membuat standardisasi format surat.'),
('163', '33', '3', 'Kemampuan mengelola kegiatan perkantoran dengan kualitas tinggi untuk memberi kenyamanan, keamanan dan keselamatan kerja. Contoh: melaksanakan pembagian ruang perkantoran dengan berpedoman kepada SOP.'),
('164', '33', '4', 'Kemampuan menganalisa dan mengevaluasi pengelolaan fasilitas perkantoran tanpa bantuan atau instruksi untuk menghasilkan kinerja yang lebih efisien. Contoh: secara berkala mengevaluasi kebutuhan ruangan kerja di seluruh lokasi perusahaan.'),
('165', '33', '5', 'Kemampuan memformulasi/memodifikasi pendekatan atau strategi pengelolaan perkantoran sebagai solusi untuk mengatasi bisnis perusahaan yang kompleks. Contoh: melakukan analisis biaya vs manfaat dan merekomendasikan perubahan Standard Operating Procedures yang bertujuan pemenuhan kebutuhan secara efisien, disesuaikan dengan kondisi perusahaan.'),
('166', '34', '1', 'Kemampuan menjelaskan informasi atau fitur utama mengenai pengelolaan dokumen perusahaan, termasuk: dokumen aktif & non-aktif, pengantaran/distribusi dokumen, dll. Contoh : penjelasan mengenai sistim pengarsipan dan rute pengantaran dokumen harian.'),
('167', '34', '2', 'Kemampuan melaksanakan/menyelesaikan pekerjaan berhubungan dengan pengelolaan dokumen dan distribusinya. Contoh: melakukan pengantaran dokumen ke bagian lain yang bertanggung jawab, melakukan pengarsipan berdasarkan sistem yang sudah ditentukan oleh perusahaan.'),
('168', '34', '3', 'Kemampuan mengelola dokumen perusahaan secara prima dengan instruksi lisan atau tertulis. Contoh: mengatur seluruh dokumen yang diterima dari berbagai fungsi dan menentukan sistem distribusi atau penyimpanannya sesuai SOP, dikomunikasikan dengan fungsi terkait.'),
('169', '34', '4', 'Kemampuan menganalisa dan mengevaluasi sistem pengelolaan dokumen tanpa bantuan atau instruksi untuk menghasilkan kinerja yang lebih baik. Contoh: berdasarkan hasil evaluasi sistim yang ada dan komunikasi dengan pihak2 terkait, membuat usulan penanganan dokumen yang paling tepat untuk perusahaan.'),
('170', '34', '5', 'Kemampuan memformulasi dan memodifikasi pendekatan atau strategi pengelolaan dokumen, antara lain sebagai jaminan pemenuhan pasal-pasal kontrak. Contoh: merevisi strategi sistem pengelolaan dokumen dan pengarsipan yang bertujuan memberikan manfaat lebih kepada perusahaan dengan prinsip efektifitas dan efisiensi.'),
('171', '35', '1', 'Kemampuan memahami Bahasa Inggris dasar (Pasif), sehingga mampu menjelaskan artinya. Contoh: mampu membaca dokumen berbahasa Inggris dasar dan mengerti perintah sederhana serta memberikan respon.'),
('172', '35', '2', 'Kemampuan melakukan komunikasi dalam Bahasa Inggris dasar (Aktif). Contoh: mampu berbicara dan bercakap-cakap dalam Bahasa Inggris dasar.'),
('173', '35', '3', 'Kemampuan dalam mengelola informasi, komunikasi dan korespondensi dalam Bahasa Inggris sehingga orang lain dapat memahami – dengan penekanan pada kemampuan Ekspresi Oral. Contoh: mampu membaca, bercakap-cakap dan menulis laporan dalam Bahasa Inggris – seperti dalam tugas kesekretariatan.'),
('174', '35', '4', 'Kemampuan untuk menganalisa dan mengevaluasi pekerjaan menggunakan Bahasa Inggris – termasuk kemampuan untuk membaca dan memahami informasi dan usulan / ide yang diberikan dalam bentuk tulisan (pemahaman tulisan) dan kemampuan untuk mengkomunikasikan informasi dan ide dalam bentuk tulisan berbahasa Inggris sehingga orang lain menjadi paham (Kemampuan ekspresi dalam bentuk tulisan). Contoh: mampu mempersiapkan laporan dan melakukan presentasi dalam Bahasa Inggris maupun menterjemahkannya secara sederhana seperti dalam tugas manajerial secara umum.'),
('175', '35', '5', 'Kemampuan untuk memformulasi dan memodifikasi penggunaan kalimat dalam Bahasa Inggris – termasuk kemampuan menulis, membuat draft dan memahami tulisan serta mampu berkoresponden secara baik dalam Bahasa Inggris formal. Contoh: mampu berargumentasi dalam Bahasa Inggris secara lisan dan tulisan (korespondensi, kontrak, dsb) seperti wawancara dengan media asing dan menulis kontrak internasional.'),
('176', '36', '1', 'Kemampuan untuk memahami dan menjelaskan kontrak sederhana sekaligus mendokumentasikannya. Contoh: kemampuan menjelaskan perjanjian kerja.'),
('177', '36', '2', 'Kemampuan melakukan pelaksanaan kontrak pada umumnya sesuai pokok-pokok perjanjian di dalamnya. Contoh: mampu melaksanakan perjanjian cicilan sesuai pola (template) kontrak.'),
('178', '36', '3', 'Kemampuan mengelola kontrak dan menjalankan administrasinya didukung pengetahuan terhadap kontrak utama perusahaan.  Contoh: mampu menyusun kontrak sederhana, mampu untuk mengelola kontrak sejak penandatanganan, pelaksanaan, pembayaran hingga akhir kontrak (kontrak cicilan pembayaran).'),
('179', '36', '4', 'Kemampuan menganalisa dan mengevaluasi draft kontrak dan pengelolaannya. Contoh: mampu membuat / merevisi kontrak berdasarkan perkembangan kebutuhan perusahaan (kontrak “payment point”).'),
('180', '36', '5', 'Kemampuan memformulasi dan memodifikasi kontrak dan pelaksanannya, termasuk re-negosiasi. Contoh: mampu membuat amandemen Perjanjian Kerjasama dengan Perusahaan Mitra, menyusun draft kontrak internasional.'),
('181', '37', '1', 'Kemampuan memahami dan menjelaskan aktivitas/program kehumasan, aktifitas dan pencapaian perusahaan secara umum kepada pihak eksternal. Contoh: kemampuan mengidentifikasi audiens yang dihadapi dalam melakukan pekerjaannya menjelaskan aktifitas yang relevan.'),
('182', '37', '2', 'Kemampuan melakukan aktivitas kehumasan (event, program, production) sederhana dalam rangka membangun serta memelihara citra dan identitas korporat/perusahaan. Contoh: kemampuan memelihara dan memastikan terpasangnya identitas perusahaan (panel visi, misi, logo, dsb) dengan baik pada masing-masing unit kerja, kunjungan public ke wtp, kunjungan BOD ke kantor-kantor, produksi leaflet sederhana, dsb.'),
('183', '37', '3', 'Kemampuan mengelola program/inisiatif/aktivitas kehumasan di area kerja masing-masing, seperti merencanakan, melaksanakan dan mengarahkan program untuk menciptakan dan memelihara citra publik yang baik bagi perusahaan. Contoh: media relation, message writing, event management, print/media production, csr, internal communication, prokoler.'),
('184', '37', '4', 'Kemampuan menganalisa dan mengevaluasi program/inisiatif/aktivitas kehumasan serta mampu melakukan monitoring dan penilaian untuk perbaikan kinerja kehumasan yang dilakukannya. Contoh: public opinion tracking, efektifitas media.'),
('185', '37', '5', 'Kemampuan memformulasi dan memodifikasi program kehumasan untuk memperoleh dukungan dari para pemangku kepentingan (internal dan eksternal) – didukung pengetahuan tentang produksi media, komunikasi serta metode dan teknik distribusinya. Contoh: PR Strategy, External Relation Strategy.'),
('186', '38', '1', 'Kemampuan untuk menjelaskan langkah-langkah yang diperlukan untuk menjalankan suatu perancangan sistem aplikasi bisnis, termasuk konsep-konsep pengembangannya. Contoh: menjelaskan metode pengembangan sistem aplikasi berdasarkan SDLC (System Development Life Cycle).'),
('187', '38', '2', 'Kemampuan memetakan kebutuhan user kedalam desain aplikasi untuk keperluan modifikasi sistem aplikasi yang telah ada sebelumnya. Contoh: memetakan kebutuhan user untuk modifikasi workflow sambungan baru.'),
('188', '38', '3', 'Kemampuan mengelola kebutuhan user untuk menghasilkan rancangan sistem aplikasi bisnis yang belum ada sebelumnya. Contoh: menghasilkan rancangan sistem aplikasi cuti online dan database yang dipakai.'),
('189', '38', '4', 'Kemampuan menganalisa & mengevaluasi kebutuhan user yang kompleks untuk menhasilkan rancangan sistem aplikasi, termasuk memberikan masukan penyempurnaan untuk tujuan optimalisasi design aplikasi dan membuang proses yang redundant. Contoh: menganalisa dan mengevaluasi kebutuhan work flow perusahaan.'),
('190', '38', '5', 'Kemampuan menentukan model pengembangan aplikasi, development tools dan strategi integrasi dengan sistem yang lain, dengan fleksibilitas yang tinggi sehingga dapat mengikuti perubahan yang terjadi pada kebutuhan bisnis maupun infrastruktur IT. Contoh: merancang aplikasi berbasis SOA (Service Oriented Architecture).'),
('191', '39', '1', 'Kemampuan menjelaskan secara umum teknis dasar pemrograman dan jenis-jenis bahasa pemrograman yang ada. Contoh: mampu menjelaskan jenis-jenis metode pemrograman seperti prosedural dan object oriented programming.'),
('192', '39', '2', 'Kemampuan melakukan pemrograman sebagai modifikasi terhadap program yang sudah ada sebelumnya sesuai dengan rancangan yang telah ditetapkan oleh perancang aplikasi. Contoh: menambah kolom report.'),
('193', '39', '3', 'Kemampuan mengelola pembuatan program sesuai dengan keinginan perancang program, termasuk membuat dokumentasi program dan melakukan pengujian unit terhadap program yang dibuat. Contoh: pembuatan program Employee Self Service.'),
('194', '39', '4', 'Kemampuan menganalisa dan mengevaluasi bagian-bagian program untuk menambahkan kontrol atau validasi yang memadai. Contoh: menambahkan verifikasi data dari kemungkinan anomali data dan mengantisipasi perilaku pengguna yang tidak terduga.'),
('195', '39', '5', 'Kemampuan melakukan integrasi antara beberapa bahasa maupun jenis pemrograman untuk mendukung suatu fungsi yang kompleks. Contoh: merancang program dengan basis client – server, program berbasis web.'),
('196', '40', '1', 'Kemampuan menjelaskan kegunaan perangkat komputer. Contoh: menjelaskan fungsi keyboard, monitor, mouse dll.'),
('197', '40', '2', 'Kemampuan mengoperasikan aplikasi dasar komputer. Contoh: menggunakan Microsoft Word, Excel dan Powerpoint yang mendasar.'),
('198', '40', '3', 'Kemampuan mendayagunakan komputer secara lebih luas dan dapat menjaga keamanan data komputer. Contoh: menggunakan fitur-fitur Microsoft Word, Excel dan Powerpoint yang lebih kompleks, melindungi komputer dari virus atau melakukan instalasi printer.'),
('199', '40', '4', 'Kemampuan menganalisa kerusakan komputer, termasuk perangkat lunak dan peripheral, dan merekomendasi perbaikan. Contoh: mendeteksi dan memperbaiki kerusakan PC atau printer secara umum.'),
('200', '40', '5', 'Kemampuan merekomendasikan jenis komputer, perangkat lunak dan peripheral yang sesuai untuk kebutuhan spesifik. Contoh: memilih solusi printer yang tepat untuk kebutuhan spot billing.'),
('201', '41', '1', 'Kemampuan menjelaskan konsep jaringan komunikasi data/suara. Contoh: mampu menjelaskan dengan baik perbedaan LAN dan WAN.'),
('202', '41', '2', 'Kemampuan mengkoneksikan (setting) berbagai peralatan IT ke dalam jaringan data/suara. Contoh: mampu mengkoneksikan PC ke dalam jaringan.'),
('203', '41', '3', 'Kemampuan mengelola jaringan komunikasi data dan suara beserta perangkat-perangkat yang digunakan. Contoh: melakukan instalasi jaringan, konfigurasi beserta pengamanannya. Mampu mengoperasikan router, switch dan PABX.'),
('204', '41', '4', 'Kemampuan mengevaluasi keadaan jaringan data dan suara. Contoh: mampu memonitor dan mengelola kapasitas jaringan (bandwidth management) menggunakan QoS.'),
('205', '41', '5', 'Kemampuan merancang jaringan komunikasi, baik data dan suara secara komprehensif dan konvergensi jaringan. Contoh : mampu merancang jaringan data untuk perusahaan. Mampu menerapkan VOIP.'),
('206', '42', '1', 'Kemampuan menjelaskan bagaimana cara kerja server dan komponennya. Contoh: menjelaskan komponen yang digunakan dalam mengelola server, seperti storage, beserta fungsi-fungsinya.'),
('207', '42', '2', 'Kemampuan mengelola server beserta semua komponennya seperti storage, tape drive termasuk sistem operasinya. Contoh: melakukan instalasi dan konfigurasi sistem operasi SUN Solaris, instalasi internet proxy.'),
('208', '42', '3', 'Kemampuan mengelola server beserta semua komponennya seperti storage, tape drive termasuk sistem operasinya. Contoh: melakukan instalasi dan konfigurasi sistem operasi SUN Solaris, instalasi internet proxy.'),
('209', '42', '4', 'Kemampuan melakukan evaluasi terhadap pengelolaan server beserta kelengkapannya. Contoh: mengevaluasi apakah pengamanannya seperti log file, UPS, precision AC dan Fire Fighting System sudah mencukupi. Melakukan ‘capacity planning’.'),
('210', '42', '5', 'Kemampuan merancang server, termasuk data center dan perlengkapannya, beserta perangkat lunak dan prosedur-prosedurnya. Contoh: menerapkan ‘active directory’ untuk perusahaan.'),
('211', '43', '1', 'Berusaha untuk bekerja dengan sebaik-baiknya. - Berusaha untuk memberikan kontribusi dan hasil yang terbaik dalam melakukan pekerjaan. - Menyelesaikan pekerjaan lebih cepat dari standar waktu yang diberikan. - Memiliki tujuan dan dapat menjelaskan secara tepat sasaran kerja yang dituju atau ingin dicapai untuk diri sendiri.'),
('212', '43', '2', 'Berupaya meningkatkan kinerja. - Melakukan efisiensi kerja atau perbaikan terhadap sistem kerja/prosedur. - Melakukan pekerjaan dengan hasil yang lebih baik daripada sebelumnya. - Melakukan pekerjaan dengan standar orang lain yang lebih baik daripada dirinya. - Melakukan pekerjaan dengan mengacu pada superior performer di organisasinya.'),
('213', '43', '3', 'Menetapkan dan mencapai sasaran yang menantang. - Membandingkan atau melakukan benchmarking secara spesifik dengan kurun waktu tertentu untuk mendapatkan indikator peningkatan kinerja. - Menetapkan dan mencapai sasaran kinerja yang lebih tinggi dengan menetapkan dan menggunakan pendekatan/metode spesifik dan unik yang belum pernah dilakukan oleh siapapun sebelumnya. - Mencapai tujuan yang menantang dengan probabilitas resiko keberhasilan dan kegagalan yang seimbang, sekaligus berusaha untuk meminimalkan resiko yang mungkin terjadi.'),
('214', '43', '4', 'Mengambil keputusan dan bertindak berdasarkan analisis manfaat-biaya. - Membuat keputusan, menetapkan prioritas atau memilih sasaran berdasarkan perhitungan input dan output. - Mempertimbangkan secara eksplisit analisa perhitungan keuntungan, return-on-investment atau analisis cost-benefit yang mendasari pengambilan keputusan dalam rangka mencapai kinerja yang optimal.'),
('215', '43', '5', 'Mengambil resiko bisnis yang diperhitungkan. - Menginvestasikan sumber daya dan waktu yang signifikan dalam situasi yang tidak pasti untuk meningkatkan keuntungan organisasi, seperti mencoba membuat sistem baru untuk peningkatan kinerja yang optimal.'),
('216', '44', '1', 'Bertindak atas peluang atau masalah saat ini. - Mengambil tindakan dalam rangka menyelesaikan masalah atau tugas tanpa menunggu perintah. - Mengidentifikasi peluang yang ada saat ini dan melakukan tindakan untuk menjawab peluang itu. - Tanggap terhadap masalah yang muncul saat ini, termasuk melakukan tindakan untuk mengatasinya. - Menetapkan tindakan sesaat untuk menghindari masalah berkesinambungan. - Memahami masalah yang dihadapi saat ini serta mampu menempatkannya sebagai ancaman dimasa depan.'),
('217', '44', '2', 'Bertindak cepat dalam situasi krisis. - Sigap mengambil keputusan dan tindakan dalam keadaan krisis tanpa harus menunggu perintah. - Bertindak dalam situasi krisis tanpa menunggu informasi yang lengkap. - Melakukan tindakan di luar wewenangnya dan mau menanggung resiko pribadi untuk mengatasi kemungkinan masalah yang akan timbul tanpa merusak sistem yang berlaku.'),
('218', '44', '3', 'Mengantisipasi masalah dan menciptakan peluang jangka pendek. - Mengidentifikasi masalah yang mungkin muncul dalam kurun waktu 1 tahun mendatang yang merupakan ancaman terhadap peluang, kemudian mengambil tindakan untuk mengantisipasinya. - Mengidentifikasi dan/atau menciptakan peluang sampai 1 tahun mendatang dan mengambil tindakan proaktif untuk mewujudkannya.'),
('219', '44', '4', 'Bertindak dengan orientasi jangka menengah. - Memikirkan antisipasi yang perlu ditempuh untuk mengatasi masalah dan peluang yang mungkin muncul di 2-3 tahun mendatang. - Berpikir dengan orientasi 2-3 tahun ke depan dan mengidentifikasi peluang atau rintangan yang mungkin muncul, serta mengambil tindakan yang sesuai.'),
('220', '44', '5', 'Bertindak dengan orientasi jangka panjang. - Memikirkan antisipasi yang perlu ditempuh untuk mengatasi masalah dan peluang yang mungkin muncul di atas 3 tahun mendatang. - Berpikir dengan orientasi jauh ke depan dan mengidentifikasi peluang atau rintangan yang mungkin muncul, serta mengambil tindakan yang sesuai.'),
('221', '45', '1', 'Memperhatikan Keteraturan - Menjaga kerapian dan keteraturan tempat kerja (meja, dokumen, peralatan) sesuai dengan letaknya. - Membuat pola dan jadwal kerja yang teratur, mendokumentasikan dengan jelas dan detil, serta berdisiplin dalam memenuhinya. - Menangani dan menyelesaikan tugas dengan memperhatikan hal-hal detil'),
('222', '45', '2', 'Memperhatikan dan menjaga kualitas - Membutuhkan kejelasan sistem dan aturan kerja yang berlaku maupun kejelasan peran, tugas, data, serta informasi dalam bentuk tertulis. - Bekerja dengan mengikuti kebijakan, pedoman dan prosedur yang berlaku. - Bekerja secara teliti dan memeriksa kembali pekerjaan sendiri guna memastikan ketepatan data, informasi atau hasil kerja. - Menemukan kelemahan sistem atau data yang hilang dan mencari informasi untuk melengkapinya.'),
('223', '45', '3', 'Memantau kualitas pekerjaan orang lain atau proyek - Memantau dan memeriksa kemajuan proses kerja orang lain sesuai dengan perencanaan yang telah ditetapkan. - Memeriksa kualitas hasil kerja orang lain dalam rangka memastikan kesesuaiannya dengan standar prosedur yang berlaku. - Memantau kemajuan proyek-proyek terhadap (dibandingkan dengan) target kerja (milestones) atau tenggat waktu yang ditetapkan'),
('224', '45', '4', 'Membangun dan mengembangkan sistem - Mengembangkan suatu perencanaan dan sistem kerja bagi kemudahan pengelolaan tugas untuk pencapaian ketepatan mutu dan hasil kerja yang ditetapkan. - Membangun dan mengembangkan suatu sistem yang lebih kompleks melibatkan berbagai faktor terkait dalam rangka memperbaiki dan meningkatkan kinerja.'),
('225', '45', '5', 'Membangun dan mengembangkan sistem yang kompleks - Berorientasi kepada peningkatan kualitas data - Mempertimbangkan prioritas utama organisasi'),
('226', '46', '1', 'Menyelidiki secara pribadi - Memiliki pengetahuan dasar atas informasi atau permasalahan yang akan dicari atau diselediki lebih lanjut. - Membuat perencanaan tentang karakteristik dan spesifikasi informasi yang ingin dicari serta membuat catatan dan dokumentasi lengkap atas informasi yang diperoleh. - Terjun langsung untuk menyelidiki secara pribadi suatu masalah atau situasi di luar hal-hal yang rutin. - Mencari informasi detil dan bertanya kepada orang yang mengetahui dan memahami permasalahan sebenarnya dari suatu kejadian.'),
('227', '46', '2', 'Menggali lebih dalam - Menggunakan metoda dan teknologi pencarian informasi yang relevan, serta sesuai dengan waktu dan tempat dalam rangka mengumpulkan informasi lebih lanjut yang dibutuhkan. - Menyampaikan sejumlah pertanyaan yang bersifat menyelidik untuk mengetahui akar permasalahan, atau hal yang tersembunyi di balik isu yang diutarakan. - Mencari informasi pada nara sumber primer dan sekunder dengan menghubungi orang lain yang langsung maupun tidak langsung terlibat untuk meminta pendapat, informasi pendukung, dan pengalamannya. - Mencari informasi pada nara sumber yang tidak biasa/lazim untuk mendukung temuan-temuan informasi yang sudah diperoleh.'),
('228', '46', '3', 'Melakukan riset - Melakukan verifikasi informasi yang telah diperoleh kepada sumber-sumber lain untuk memperoleh validitas lebih lanjut. - Melakukan upaya sistematis dalam waktu yang terbatas untuk memperoleh informasi yang diperlukan mengenai kecenderungan arah industri, politik, ekonomi, teknologi, dan lain-lain. - Melakukan riset formal melalui surat kabar, majalah, jaringan komputer, atau sumber lainnya, termasuk riset pasar, keuangan, maupun pesaing.'),
('229', '46', '4', 'Menciptakan sistem sendiri - Melakukan penelusuran informasi secara lebih mendalam dari suatu penerbitan atau nara sumber. - Melakukan kunjungan atau memeriksa lokasi untuk mengenali suasana dan mempelajari informasi maupun hal-hal yang terkait secara seksama'),
('230', '46', '5', 'Mengalokasikan sumberdaya untuk melakukan penelusuran informasi - Mengatur dan mengundang orang lain secara informal dengan pola yang terjadwal/periodik untuk mengumpulkan informasi yang dibutuhkan. - Melibatkan dan meminta bantuan orang lain yang tidak secara resmi terlibat dalam lingkup pekerjaannya untuk mencarikan informasi yang diperlukan.'),
('231', '47', '1', 'Menindak-lanjuti secara cepat dan tepat. - Menanggapi dan menindak-lanjuti dengan cepat dan tepat setiap permintaan, pertanyaan maupun keluhan pelanggan. - Menepati janji, menjaga tata krama, serta bersikap ramah dan menyenangkan dalam berhubungan dan melayani pelanggan. - Memelihara komunikasi dua arah dengan pelanggan secara periodik guna memasti-kan harapan kedua belah pihak melalui upaya berbagi informasi terkini yang relevan.'),
('232', '47', '2', 'Mengambil tanggung jawab sendiri atas pelayanan pelanggan. - Menyelesaikan keluhan dan masalah pelanggan secara suportif dan kooperatif sesuai dengan peraturan yang berlaku. - Berani bertanggung-jawab dalam menyelesaikan permasalahan dengan pelanggan. - Memberikan pelayanan kepada pelanggan dengan melibatkan diri demi memberikan kemudahan dalam situasi kritis, seperti menyediakan waktu lebih untuk pelanggan pada saat dibutuhkan.'),
('233', '47', '3', 'Bertindak lebih untuk pelanggan. - Memberikan layanan yang bernilai tambah melebihi standar yang berlaku dan bertindak lebih dari yang biasa diharapkan pelanggan serta berinisiatif mengetahui faktor-faktor yang dapat mempengaruhi kepuasan pelanggan. - Menjaga hubungan baik dengan pelanggan melalui komunikasi yang intens dan berkesinambungan serta mengungkapkan ekspektasi positif kepada pelanggan.'),
('234', '47', '4', 'Menanggapi kebutuhan mendasar pelanggan. - Memahami aktivitas atau bidang usaha pelanggan dan mencari informasi mengenai kebutuhan mendasar yang sesungguhnya dari pelanggan, lebih dari apa yang pernah disampaikan pelanggan tersebut. - Memodifikasi jasa atau produk yang ada sesuai kebutuhan mendasar pelanggan dengan menggunakan perspektif pelanggan. - Bertindak untuk kepentingan dan kemajuan pelanggan dengan memberikan saran dan masukan di luar dari harapan pelanggan.'),
('235', '47', '5', 'Menggunakan perspektif jangka panjang. - Bekerja dengan menggunakan perspektif jangka panjang dalam menjawab masalah pelanggan. - Bersedia mengeluarkan biaya saat ini demi hubungan jangka panjang yang saling menguntungkan. - Bertindak sebagai penasehat terpercaya, terlibat dalam proses pengambilan keputusan di pihak pelanggan. - Memformulasikan sistem pelayanan pelanggan yang komprehensif untuk menghasilkan tujuan jangka panjang dan kepentingan nilai lebih bersama.'),
('236', '48', '1', 'Memahami emosi orang lain - Bersedia mendengarkan ungkapan pikiran, perasaan atau masalah orang lain. - Peka menangkap sinyal dan memahami emosi yang sedang terjadi pada orang lain maupun pesan eksplisit yang disampaikan melalui bahasa tubuh, mimik wajah, nada bicara, dan lain-lain. - Memberikan tanggapan atas ungkapan emosi yang disampaikan orang lain secara tidak langsung. - Secara aktif berupaya memahami emosi dan permasalahan orang lain, menawarkan kesediaan untuk mendengarkan keluhan orang lain.'),
('237', '48', '2', 'Memahami emosi dan ekspresi yang diucapkan - Memahami apa yang diucapkan dan dirasakan orang lain mengenai suatu isu, topik, permasalahan atau hal lainnya. - Memberikan tanggapan atas pernyataan verbal yang disampaikan orang lain'),
('238', '48', '3', 'Memahami makna - Menyimak ungkapan yang disampaikan orang lain dalam rangka ingin memahami makna atau arti yang tidak diekspresikan atau dikatakan dengan jelas. - Memprediksi tanggapan orang lain yang didasari oleh kemampuan mendengarkan dan mengobservasi ungkapan orang tersebut. - Mengajukan beberapa pertanyaan lebih mendalam sebagai konfirmasi guna meningkatkan pemahaman makna atas pesan yang disampaikan'),
('239', '48', '4', 'Mengenali masalah yang mendasar - Memahami berbagai penyebab yang mendasari munculnya tindakan, perilaku, dan permasalahan seseorang. - Mengenali karakteristik kepribadian orang lain baik yang merupakan kekuatan maupun kelemahan.'),
('240', '48', '5', 'Mengenali masalah kompleks yang mendasar - Mengenali penyebab dari perilaku jangka panjang seseorang, pola perilaku atau permasalahannya'),
('241', '49', '1', 'Membujuk secara langsung orang atau sekelompok kecil orang - Menggunakan beberapa cara untuk membujuk orang lain tanpa menyesuaikannya dengan latar belakang tingkatan atau minat mereka. - Membujuk secara langsung melalui diskusi menggunakan data dan informasi pendukung. - Melakukan tindakan membujuk menggunakan sarana presentasi yang dilengkapi dengan berbagai alasan, data, contoh serta alat peraga.'),
('242', '49', '2', 'Melakukan adaptasi dengan hadirin atau lingkungan unit kerjanya - Memodifikasi presentasi atau diskusi untuk menyesuaikan dan menarik minat hadirin. - Memperhitungkan reaksi orang lain serta dampaknya kepada mereka atas tindakan-tindakan atau perkataannya. - Mengambil tindakan dramatis/luar biasa dan atau terencana untuk menimbulkan kesan tertentu pada hadirin'),
('243', '49', '3', 'Menggunakan pengaruh tidak langsung dalam tingkatan korporasi - Mengambil serangkaian pendekatan dan tindakan untuk mempengaruhi orang lain secara tidak langsung. - Melibatkan pihak ketiga dalam mempengaruhi dan meyakinkan orang lain untuk menerima gagasannya.'),
('244', '49', '4', 'Menggunakan strategi mempengaruhi kelompok dalam cakupan agak luas (setingkat provinsi) - Membangun dukungan untuk mempengaruhi orang lain atau pihak tertentu. - Mengembangkan strategi pendekatan yang kompleks seperti melakukan loby-loby terhadap berbagai pihak terkait dalam rangka memperoleh dukungan. - Mengelola informasi secara bijaksana untuk memperoleh efek tertentu kepada orang lain. - Memberdayakan interaksi kelompok dalam rangka mengarahkan mereka pada tujuan tertentu'),
('245', '49', '5', 'Menggunakan strategi mempengaruhi kelompok dalam cakupan luas (setingkat nasional) - Membangun dukungan untuk mempengaruhi orang lain atau pihak tertentu. - Mengembangkan strategi pendekatan yang kompleks seperti melakukan loby-loby terhadap berbagai pihak terkait dalam rangka memperoleh dukungan. - Mengelola informasi secara bijaksana untuk memperoleh efek tertentu kepada orang lain. - Memberdayakan interaksi kelompok dalam rangka mengarahkan mereka pada tujuan tertentu'),
('246', '50', '1', 'Memahami struktur formal - Mengenali atau menggunakan struktur formal atau hirarki organisasi. - Memahami rantai perintah dan kekuasaan atau wewenang dalam organisasi. - Mengerti peraturan, sistem dan prosedur yang berlaku. - Memahami struktur organisasi formal seperti SOP dan Peraturan Perusahaan'),
('247', '50', '2', 'Memahami struktur informal - Memahami struktur organisasi informal seperti mengidentifikasi tokoh utama, orang-orang yang mempengaruhi pengambilan keputusan di perusahaan. - Menggunakan rantai komando untuk memperbaiki keadaan. - Mengenali keterbatasan organisasi yang tidak tampak, apa yang mungkin dan tak mungkin pada keadaan tertentu.'),
('248', '50', '3', 'Memahami iklim dan budaya organisasi - Mengenali keterbatasan organisasi yang tidak dikemukakan - apa yang mungkin dan tidak mungkin pada keadaan atau posisi tertentu - Memahami dan menggunakan budaya dan bahasa perusahaan untuk mendapatkan tanggapan terbaik.'),
('249', '50', '4', 'Memahami politik perusahaan - Memahami, menggambarkan dan menggunakan kekuasaan dan hubungan politik dalam perusahaan (aliansi, persaingan) untuk mendapatkan dampak yang positif bagi perusahaan'),
('250', '50', '5', 'Memahami isu mendasar perusahaan - Mengenali peluang dan masalah yang sedang dihadapi organisasi, individu maupun organisasi terkait. - Mampu mengantisipasi tindakan yang diperlukan untuk membuat peluang atau menghindari masalah di masa depan dengan adanya perubahan kekuatan di dalam organisasi. - Memahami isu jangka panjang organisasi yang terpendam'),
('251', '51', '1', 'Melakukan kontak formal dalam hubungan kerja. - Aktif mengenali orang-orang yang ada dalam lingkungan organisasinya. - Berinisiatif untuk melakukan kontak formal melalui berbagai sarana komunikasi atau mengunjungi orang lain yang memiliki hubungan bisnis - Aktif melakukan hubungan atau pertemuan formal dengan mitra bisnis, asosiasi atau forum yang relevan.'),
('252', '51', '2', 'Membina hubungan baik - Membina hubungan baik dengan bersikap ramah, sopan dan suportif kepada mitra kerja, atasan, bawahan, kenalan, serta pelanggan. - Membina hubungan persahabatan dengan relasi, pelanggan, atau pihak lain melalui kegiatan di luar kantor seperti di klub, restoran atau tempat olah raga. - Berinisiatif mengadakan acara-acara yang dapat meningkatkan intensitas hubungan baik dengan orang lain, seperti: pesta, pertemuan keluarga, reuni, dan lain-lain'),
('253', '51', '3', 'Sesekali melakukan kontak sosial - Berpartisipasi dalam kegiatan sosial atau organisasi profesi dalam rangka menjalin dan mengembangkan jejaring bisnis. - Mengunjungi sport club, event-event yang memungkinkan untuk mmperluas jejaring untuk kepentingan bisnis'),
('254', '51', '4', 'Rutin melakukan kontak sosial - Berpartisipasi secara aktif dalam kegiatan sosial atau organisasi profesi dalam rangka menjalin dan mengembangkan jejaring bisnis. - Member dari sport club atau komunitas yang memungkinkan untuk mmperluas jejaring untuk kepentingan bisnis'),
('255', '51', '5', 'Membina hubungan pribadi - Menciptakan persahabatan yang erat termasuk dalam hal ini, sering mengundang kenalan dan relasi bisnis ke rumah atau mengunjungi mereka di rumahnya. - Membina hubungan yang saling menguntungkan dengan berbagai pihak untuk kemanfaatan jangka panjang dan perluasan jejaring bisnis'),
('256', '52', '1', 'Berpartisipasi dalam kelompok. - Berpartisipasi menjadi anggota kelompok yang suportif dengan mengerjakan bagian tugasnya dan mendukung keputusan kelompok. - Meminta sesama anggota kelompok untuk bekerjasama dan berperan aktif serta berkontribusi untuk keberhasilan kelompok. - Mengupayakan agar sesama anggota kelompok senantiasa memperoleh informasi mengenai perkembangan yang sudah dicapai oleh kelompok. - Berbagi informasi yang relevan atau bermanfaat untuk anggota lain kelompoknya.'),
('257', '52', '2', 'Mengungkapkan sikap dan harapan yang positif. - Menyatakan harapan yang positif tentang orang lain dalam hal kemampuan, peran yang diharapkan, dan sebagainya. - Membicarakan anggota kelompok secara positif baik secara langsung pada kelompoknya maupun pada pihak lain.'),
('258', '52', '3', 'Menghargai masukan anggota lain. - Meminta pandangan dan pendapat kepada semua anggota kelompok untuk membantu membuat keputusan. - Mau belajar dari anggota kelompok termasuk dari bawahan dan/atau mitra kerja. - Menghargai secara sungguh-sungguh masukan/pendapat serta keahlian orang lain untuk bersama-sama memecahkan konflik atau permasalahan kelompok.'),
('259', '52', '4', 'Memberikan dorongan. - Memberi semangat dan memberdayakan kapasitas sesama anggota kelompok agar mereka merasa kuat dan penting. - Secara terbuka memberi penghargaan kepada anggota kelompok yang berkinerja baik.'),
('260', '52', '5', 'Membangun komitmen kelompok. - Menjaga reputasi kelompok dan mempromosikannya kepada pihak-pihak lain di luar kelompok. - Menciptakan suasana kerjasama kelompok yang akrab, obyektif dan kondusif, seperti mengadakan pertemuan atau event bersama. - Membangun perilaku/moral kelompok yang baik melalui berbagai aktivitas informal, seperti menetapkan identitas kelompok. - Mengupayakan penyelesaian dan solusi atas perselisihan/konflik yang terjadi dalam kelompok serta mengangkat kemanfaatan dari solusi yang dipilih.'),
('261', '53', '1', 'Memastikan anggota kelompok memperoleh informasi. - Memastikan anggota kelompok telah mendapatkan informasi yang diperlukan. - Menjelaskan alasan suatu pengambilan keputusan yang menyangkut atau berdampak terhadap kelompok. - Memberikan kejelasan tentang sasaran kelompok dan bagaimana kontribusi setiap peran anggota dalam mencapai sasaran tersebut. - Memastikan anggota kelompoknya mencapai target dan agenda kerja yang telah disepakati bersama.'),
('262', '53', '2', 'Meningkatkan efektivitas kelompok. - Menyadarkan dan mengenalkan potensi masing-masing anggota kelompok dalam rangka optimalisasi peran dan fungsi mereka untuk kepentingan bersama. - Mengatur, mengarahkan, membimbing, memotivasi, mengawasi anggota kelompok serta memberdayakan kapasitas mereka bagi peningkatan kinerja. - Menggunakan strategi yang tepat untuk menciptakan moral dan menjaga produktifitas kerja kelompok (termasuk merekrut, menseleksi, memelihara, mengembangkan, memberkan sanksi ataupun mengeluarkan anggota kelompok).'),
('263', '53', '3', 'Menjaga kelompok dan reputasinya. - Menjalankan strategi kepemimpinan yang tepat untuk menjaga kepentingan dan reputasi kelompok, serta meningkatkan produktifitas maupun efektivitas kerja kelompok. - Memastikan kebutuhan dasar kelompok dapat terpenuhi (sdm, informasi dan sumber daya lainnya). - Memastikan anggota kelompok dapat menerima misi, sasaran, agenda, iklim dan kebijakan organisasi termasuk pemberian sanksi dan reward.'),
('264', '53', '4', 'Pemimpin yang menjadi teladan. - Menggunakan otoritas dan kekuasaan formal secara wajar dan obyektif. - Menetapkan norma bagi perilaku kelompok dan memberikan sanksi bagi yang melanggar norma tersebut. - Memastikan bahwa orang lain mengikuti misi, sasaran, agenda, iklim dan kebijakan organisasi termasuk pemberian sanksi dan reward yang dikembangkan, dengan cara memberi contoh yang baik.'),
('265', '53', '5', 'Mengkomunikasikan suatu visi yang menantang. - Menyampaikan visi yang menantang secara menarik dan mengedukasi misi dan nilai-nilai kelompok yang mendorong timbulnya antusiasme, energi dan komitmen kelompok, seperti halnya pemimpin yang kharismatik (contoh kasus ini sangat jarang, dan biasanya baru terlihat berdasarkan hasil suatu pekerjaan).'),
('266', '54', '1', 'Menyatakan harapan yang positif - Menunjukkan dan menyatakan kepercayaan bahwa setiap individu mempunyai keinginan dan kemampuan belajar untuk peningkatan kinerja. - Memberikan pandangan atau menyatakan harapan yang positif tentang potensi pengembangan diri seseorang di masa mendatang. - Memberikan keyakinan dan motivasi kepada orang lain yang mengalami kegagalan'),
('267', '54', '2', 'Memberikan instruksi jangka pendek - Memberikan instruksi yang jelas dan deskriptif kepada orang lain tentang hal-hal yang harus dikerjakan serta contoh-contoh konkrit mengenai cara-cara mengerjakan sesuatu. - Memberi saran-saran tentang proses pengerjaan sesuatu yang spesifik dan bermanfaat'),
('268', '54', '3', 'Memberikan penjelasan dan dukungan - Memberi arahan, penjelasan dan rasionalisasi strategi pelatihan, pembelajaran dan pengembangan. - Memberi dukungan praktis untuk memudahkan pekerjaan orang lain/ bawahan, antara lain dengan menyediakan tambahan sumber daya, alat, informasi, saran ahli dan lain-lain. - Mengajukan pertanyaan, memberi tes atau menggunakan metode lain untuk memastikan orang lain telah memahami penjelasan yang diberikan.'),
('269', '54', '4', 'Memberikan umpan balik - Memberikan tanggapan atau umpan balik kepada`masing-masing individu yang spesifik, positif maupun negatif, serta saran pribadi untuk tujuan pengembangan lebih lanjut. - Mengungkapkan harapan positif kepada orang lain akan kinerjanya di masa mendatang ketika memberikan umpan balik untuk perbaikan'),
('270', '54', '5', 'Melakukan bimbingan (coaching, mentoring atau training) secara mendalam - Mengidentifikasi kebutuhan spesifik masing-masing individu akan program pelatihan dan pengembangan, baik formal maupun non formal. - Mengatur pembagian dan pendelegasian tugas, mengkoordinir program pelatihan formal atau hal lain yang sesuai untuk proses pembelajaran dan pengembangan karir seseorang'),
('271', '55', '1', 'Memberi petunjuk - Memberi petunjuk dan penjelasan tentang tugas-tugas secara rinci agar dapat diperoleh pemahaman yang sama. - Memberitahukan dengan jelas persyaratan yang dibutuhkan untuk penanganan suatu tugas. - Menegaskan rincian tugas rutin yang perlu mendapat perhatian karena memiliki hal-hal yang lebih bernilai atau lebih berjangka panjang'),
('272', '55', '2', 'Menentukan batasan - Menentukan batasan-batasan perilaku yang harus diperhatikan atau digunakan. - Bersikap asertif dan berani secara tegas menolak permintaan atau pendapat yang tidak masuk akal. - Mengolah situasi sedemikian rupa untuk membatasi pilihan orang lain dalam rangka menyederhanakan permasalahan atau mengatasi situasi yang berkepanjangan'),
('273', '55', '3', 'Menuntut kinerja yang tinggi - Menetapkan standar kinerja yang tinggi secara sepihak. - Meminta kepatuhan atas perintah, penugasan atau permintaannya dengan memastikan diterapkannya pengawasan yang ketat. - Menuntut kualitas kerja dan sumber daya yang baik kepada orang lain'),
('274', '55', '4', 'Memonitor kinerja - Membandingkan secara terbuka kinerja seseorang dengan standar yang telah ditetapkan. - Melakukan pengawasan dalam rangka memastikan kinerja dapat dicapai sesuai standar yang telah disepakati.'),
('275', '55', '5', 'Mengkonfrontasi dan memberikan konsekwensi dari perilaku yang muncul - Mengkonfrontasi orang lain secara terbuka dan langsung dalam masalah kinerja. - Menetapkan konsekuensi (reward & punishment) atas kinerja seseorang yang dibandingkan dengan standar yang berlaku. - Menggunakan kekuatan dan kapasitas yang dimiliki untuk secara tegas mengarahkan lingkungan dalam rangka memastikan kepentingan jangka panjang organisasi dapat tercapai.'),
('276', '56', '1', 'Mampu menahan diri, tidak menuruti kata hati dalam mengekspresikan emosi.'),
('277', '56', '2', 'Mengenali dan mengelola emosi diri secara tepat dan terhindar dari frustrasi.'),
('278', '56', '3', 'Memberikan respon dengan emosi yang tenang ketika dihadapkan pada situasi, masalah yang menekan sehingga tidak mengganggu unjuk kerja.'),
('279', '56', '4', 'Mengelola stress secara efektif sehingga tidak memberikan pengaruh negative terhadap hasil kerja individual maupun kelompok.'),
('280', '56', '5', 'Mengendalikan emosi secara konstrutif dengan memberikan pengaruh yang membangun kepada lingkungannya serta memberi ketenangan bagi orang lain disekitarnya sekalipun dalam situasi yang mengancam dirinya.'),
('281', '57', '1', 'Menguraikan masalah. - Menguraikan masalah sederhana dan membaginya dalam bagian-bagian yang lebih kecil tanpa mengaitkannya dengan prioritas. - Memilah masalah menjadi bagian-bagian lebih kecil agar mudah dipahami. - Mengenali suatu permasalahan dengan menggunakan hubungan sebab akibat dan mengkaji alternatif yang timbul dari suatu persoalan'),
('282', '57', '2', 'Melihat dan membuat hubungan tunggal atau sederhana/dasar. - Menyusun daftar tugas berdasarkan prioritas kepentingannya. - Membuat hubungan yang sederhana atau tunggal antar bagian-bagian misalnya A mengakibatkan B. - Memisahkan masalah menjadi dua bagian: pro dan kontra. - Memahami masalah berdasarkan hubungan sebab-akibat atau konsekuensi dari suatu tindakan.'),
('283', '57', '3', 'Melihat dan membuat hubungan majemuk dan kompleks. - Membuat hubungan majemuk sebab-akibat yang kompleks dalam menyelesaikan masalah dengan menggunakan berbagai pola pendekatan seperti rantai panjang, tulang ikan dan sebagainya. - Mengidentifikasi beberapa faktor penyebab yang potensial atas suatu permasalahan.'),
('284', '57', '4', 'Membuat analisa yang sangat kompleks. - Menggunakan berbagai teknik analisa untuk mengidentifikasi dan menguraikan masalah yang kompleks dalam rangka menetapkan alternatif pemecahan masalah dan melihat konsekuensi-konsekuensi dari pemecahan masalah yang diambil. - Mengidentifikasi dan menganalisa masalah yang kompleks, kemudian menetapkan beberapa alternatif solusi pemecahan masalah yang disertai dengan bobot atas pilihan setiap alternatif dan merencanakan aplikasi dari pemecahan masalah yang dipilih.'),
('285', '57', '5', 'Menciptakan teori atau pendekatan baru untuk melakukan analisa terhadap persoalan yang kompleks.'),
('286', '58', '1', 'Menguasai bidang tugas yang menjadi keahliannya dan ditampakkan pada kesehariannya'),
('287', '58', '2', 'Diakui kepakarannya di tingkat Unit Kerja'),
('288', '58', '3', 'Diakui kepakarannya di tingkat Organisasi'),
('289', '58', '4', 'Diakui kepakarannya di tingkat Nasional'),
('290', '58', '5', 'Diakui kepakarannya di tingkat International'),
('291', '59', '1', 'Bersikap positif di dalam menghadapi perbedaan dan perubahan.'),
('292', '59', '2', 'Dapat menyesuaikan diri dengan perubahan ditempat kerja maupun organisasi secara keseluruhan.'),
('293', '59', '3', 'Dapat menyesuaikan diri dengan pendekatan-pendekatan positif yang diharapkan oleh lingkungannya.'),
('294', '59', '4', 'Mengelola perubahan-perubahan di tempat kerja dengan menjadi agen perubahan.'),
('295', '59', '5', 'Membuat langkah-langkah terobosan untuk jangka panjang dalam mempersiapkan organisasi dan individu menuju perubahan.'),
